using System;
using System.Collections.Generic;
using System.Windows.Forms;

using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Reporting.WinForms;

using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using PEA.BPM.AgencyManagementModule.Utilities;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.Architecture.CommonUtilities;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.Architecture.PrintUtilities;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule
{
    [SmartPart]
    public partial class CAB02_01ReportView : UserControl, ICAB02_01ReportView
    {
        List<CAB02_HederReportInfo> _detailReport;

        public CAB02_01ReportView()
        {
            InitializeComponent();

        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAB02_01ReportViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }

        /// <summary>
        /// 
        /// </summary>
        public void ShowReport(CAB02_HederReportInfo dataReport)
        {
            try
            {
                ReportParameter[] _p = new ReportParameter[6];
                _p[0] = new ReportParameter("AgencyIdFrom", dataReport.AgencyIdFrom.Substring(4));
                _p[1] = new ReportParameter("AgencyIdTo", dataReport.AgencyIdTo.Substring(4));
                _p[2] = new ReportParameter("PeriodFrom", dataReport.PeriodFrom);
                _p[3] = new ReportParameter("PeriodTo", dataReport.PeriodTo);
                _p[4] = new ReportParameter("BranchName", dataReport.BranchName);
                _p[5] = new ReportParameter("PrintDate", dataReport.PrintDate);
                
                ReportDataSource sReportDataSourceDetail = new ReportDataSource();
                this.rptViewer.Reset();
                this.rptViewer.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                this.rptViewer.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAB02_01.rdlc";          
                sReportDataSourceDetail.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_CAB02_DetailReportInfo";
                sReportDataSourceDetail.Value = dataReport.DataList;
                this.rptViewer.LocalReport.SetParameters(_p);
                this.rptViewer.LocalReport.DataSources.Add(sReportDataSourceDetail);

                if (dataReport.PrintPreview)
                {
                    this.rptViewer.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.rptViewer.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }
            }
            catch (Exception ex)
            {
                throw ex;
            }
        }

    }
}

