using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using System.Data;
using PEA.BPM.AgencyManagementModule.DataSet;
using System.Collections.Generic;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using Microsoft.Reporting.WinForms;
using System.Globalization;

using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.Architecture.CommonUtilities;
using PEA.BPM.Architecture.PrintUtilities;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule.ReportViews
{
    [SmartPart]
    public partial class CAB03_01ReportView : UserControl, ICAB03_01ReportView
    {
        public CAB03_01ReportView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAB03_01ReportViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }

        public void ShowReport(List<ReturnBillBookBillPasteStatusReportInfo> _myList, bool printPreview)
        {
            try
            {
                string billBookId = "";               
                ReportParameter[] ps = new ReportParameter[6];

                if (_myList.Count > 0)
                { 
                    ReturnBillBookBillPasteStatusReportInfo row =  _myList[0];
                                   
                    if (row.RefBillBookId.Length == ModuleConfigurationNames.BillBookIdLength)
                    {
                        billBookId = row.RefBillBookId.Substring(6, row.RefBillBookId.Length - 6);
                    }
                    else
                    {
                        billBookId = row.RefBillBookId;
                    }

                    ps[0] = new ReportParameter("PrintDate", row.PrintDate);
                    ps[1] = new ReportParameter("BranchName", String.Format("{0}, {1}", row.BranchId, row.BranchName));
                    ps[2] = new ReportParameter("Period", row.CheckInDate);
                    ps[3] = new ReportParameter("AgencyTaxNo", row.TaxNo);
                    ps[4] = new ReportParameter("BillBookNo", billBookId);
                    ps[5] = new ReportParameter("AgencyName",  row.AgentName);                                                                     
                }                           

                this.customerNotPaidAgencyReport.Reset();
                this.customerNotPaidAgencyReport.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                this.customerNotPaidAgencyReport.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAB03_01.rdlc";
                ReportDataSource customerNotPaidAgency = new ReportDataSource();
                customerNotPaidAgency.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_ReturnBillBookBillPasteStatusReportInfo";
                customerNotPaidAgency.Value = _myList;
                
                customerNotPaidAgencyReport.LocalReport.DataSources.Add(customerNotPaidAgency);
                customerNotPaidAgencyReport.LocalReport.SetParameters(ps);
                this.customerNotPaidAgencyReport.Width = this.Width;
                this.customerNotPaidAgencyReport.Height = this.Height;

                if (printPreview)
                {
                    this.customerNotPaidAgencyReport.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.customerNotPaidAgencyReport.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }

            }
            catch (Exception ex)
            {
                throw ex;
            }              
        }
    }
}

