        public void ShowReport(CAB03_HeaderReportInfo header, CAB03_02DetailReportInfo _myDetail, bool printPreview)
        {
            try
            {                
                dsAR ds = new dsAR();
               
                ReportParameter[] ps = new ReportParameter[10];               
                ps[0] = new ReportParameter("BranchName", header.BranchName);
                ps[1] = new ReportParameter("ReportDate", header.Period);
                ps[2] = new ReportParameter("PrintDate", header.PrintDate);
                ps[3] = new ReportParameter("AgencyTaxNo", header.AgencyTaxNo);
                ps[4] = new ReportParameter("AgencyName", header.AgencyName);
                ps[5] = new ReportParameter("BillBookId", header.BillBookNo.Substring(ModuleConfigurationNames.BranchCodeLength, ModuleConfigurationNames.BillBookLengthOnly));
                if (_myDetail.TotalAmount.Value == 0)
                    ps[6] = new ReportParameter("TotalAmount", String.Format("{0} 불 ({1})", "0.00", StringConvert.ConvertAmountToText(_myDetail.TotalAmount.Value.ToString("0.##"))));
                else
                    ps[6] = new ReportParameter("TotalAmount", String.Format("{0} 불 ({1})", _myDetail.TotalAmount.Value.ToString("#,###.##"), StringConvert.ConvertAmountToText(_myDetail.TotalAmount.Value.ToString("####.##"))));
                ps[7] = new ReportParameter("CashAmount", _myDetail.CashAmount.Value.ToString());
                ps[8] = new ReportParameter("ChequeAmount", _myDetail.ChequeAmount.Value.ToString());
                ps[9] = new ReportParameter("TotalNetAmount", _myDetail.TotalNetAmount.Value.ToString());

                this.billStatusReport.Reset();
                this.billStatusReport.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                
                ReportDataSource sBillStatusDetailReport = new ReportDataSource();
                sBillStatusDetailReport.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_BillInfoInEachBillBookIdInfo";
                sBillStatusDetailReport.Value = _myDetail.BillLineItem;                
                this.billStatusReport.LocalReport.DataSources.Add(sBillStatusDetailReport);

                ReportDataSource sBillBookPrePaid = new ReportDataSource();
                sBillBookPrePaid.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_BillBookPrePaidInfo";
                sBillBookPrePaid.Value = _myDetail.PrePaidItem;               
                this.billStatusReport.LocalReport.DataSources.Add(sBillBookPrePaid);
                
                this.billStatusReport.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAB03_02.rdlc";                                                                           
                billStatusReport.LocalReport.SetParameters(ps);
            



                if (printPreview)
                {
                    this.billStatusReport.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.billStatusReport.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }
            }
            catch (Exception ex)
            {
                throw ex;
            }
        }

