using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using System.Collections.Generic;
using System.Collections;
using PEA.BPM.Architecture.PrintUtilities;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using Microsoft.Reporting.WinForms;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule
{
    [SmartPart]
    public partial class CAB03_03ReportView : UserControl, ICAB03_03ReportView
    {
        public CAB03_03ReportView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAB03_03ReportViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }


        /// <summary>
        /// 
        /// </summary>
        public void ShowReport(CAB03_HeaderReportInfo header, List<CAB03_DetailReportInfo> detail)
        {
            try
            {
                ReportParameter[] ps = new ReportParameter[6];

                ps[0] = new ReportParameter("PrintDate", header.PrintDate);
                ps[1] = new ReportParameter("BranchName", header.BranchName);
                ps[2] = new ReportParameter("Period", header.Period);
                ps[3] = new ReportParameter("AgencyTaxNo", header.AgencyTaxNo);
                ps[4] = new ReportParameter("BillBookNo", header.BillBookNo.Substring(6,ModuleConfigurationNames.BillBookLengthOnly));
                ps[5] = new ReportParameter("AgencyName", header.AgencyName);

                this.rptViewer.Reset();
                this.rptViewer.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;

                ReportDataSource sReportDataSource = new ReportDataSource();
                sReportDataSource.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_CAB03_DetailReportInfo";
                sReportDataSource.Value = detail;
                this.rptViewer.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAB03_03.rdlc";
                this.rptViewer.LocalReport.SetParameters(ps);
                this.rptViewer.LocalReport.DataSources.Add(sReportDataSource);

                if (header.PrintPreview)
                {
                    this.rptViewer.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.rptViewer.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }
            }
            catch (Exception ex)
            {
                throw ex;
            }
        }
    }
}

