using System;
using System.Windows.Forms;
using System.Collections.Generic;
using System.Data;
using System.Globalization;

using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Reporting.WinForms;

using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using PEA.BPM.AgencyManagementModule.DataSet;
using PEA.BPM.Architecture.CommonUtilities;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.Architecture.PrintUtilities;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule.ReportViews
{
    [SmartPart]
    public partial class CAB04_03ReportView : UserControl, ICAB04_03ReportView
    {
        public CAB04_03ReportView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAB04_03ReportViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }


        public void ShowReport(CAB04_03HeaderReportInfo header, List<CAB04_03DetailReportInfo> detail)
        {
            try
            {
                if (detail.Count == 0)
                {
                    for (int i = 0; i < 10; i++)
                    {
                        CAB04_03DetailReportInfo item = new CAB04_03DetailReportInfo();
                        detail.Add(item);
                    }
                }
             
                ReportParameter pPrintDate = new ReportParameter();
                ReportParameter pBranchName = new ReportParameter();
                ReportParameter pAgencyName = new ReportParameter();
                ReportParameter pPeriod = new ReportParameter();
                ReportParameter pTimeNo = new ReportParameter();
                ReportParameter pRegisterDate = new ReportParameter();
                ReportParameter pCalculateDate = new ReportParameter();

                pPrintDate = new ReportParameter("PrintDate", header.PrintDate);
                pBranchName = new ReportParameter("BranchName", header.BranchName);
                pAgencyName = new ReportParameter("AgencyName", header.AgencyName);
                pPeriod = new ReportParameter("Period", header.Period);
                pTimeNo = new ReportParameter("TimeNo", header.TimeNo);
                pRegisterDate = new ReportParameter("RegisterDate", header.RegisterDate);
                pCalculateDate = new ReportParameter("CalculateDate", header.CalculateDate);

                ReportDataSource sRegistryCommissionReport = new ReportDataSource();
                this.registryCommissionDetailReport.Reset();
                this.registryCommissionDetailReport.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAB04_03.rdlc";
                sRegistryCommissionReport.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_CAB04_03DetailReportInfo";
                sRegistryCommissionReport.Value = detail;

                ReportParameter[] ps = new ReportParameter[] { pPrintDate, pBranchName, pAgencyName, pPeriod, pTimeNo, pRegisterDate, pCalculateDate };
                registryCommissionDetailReport.LocalReport.SetParameters(ps);
                registryCommissionDetailReport.LocalReport.DataSources.Add(sRegistryCommissionReport);
                this.registryCommissionDetailReport.Width = this.Width;
                this.registryCommissionDetailReport.Height = this.Height;

                if (header.PrintPreview)
                {
                    this.registryCommissionDetailReport.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.registryCommissionDetailReport.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }
            }
            catch (Exception ex)
            {
                throw;
            }
        }
    }
}

