using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using System.Collections.Generic;
using Microsoft.Reporting.WinForms;
using PEA.BPM.Architecture.PrintUtilities;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule
{
    [SmartPart]
    public partial class CAB05_01ReportView : UserControl, ICAB05_01ReportView
    {
        public CAB05_01ReportView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAB05_01ReportViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }
        public void ShowReport(CAB05_01HeaderReportInfo header, List<CAB05_01DetailReportInfo> detail)
        {
            try
            {              
                ReportParameter pPrintDate = new ReportParameter();
                ReportParameter pBranchName = new ReportParameter();               

                pPrintDate = new ReportParameter("pPrintDate", header.PrintDate);
                pBranchName = new ReportParameter("pBranchName", header.BranchName);
               
           

                ReportDataSource sCAB05_01 = new ReportDataSource();
                this.rptCAB05_01.Reset();
                this.rptCAB05_01.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                this.rptCAB05_01.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAB05_01.rdlc";
                sCAB05_01.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_CAB05_01DetailReportInfo";
                sCAB05_01.Value = detail;

                ReportParameter[] ps = new ReportParameter[] { pPrintDate, pBranchName};
                rptCAB05_01.LocalReport.SetParameters(ps);
                rptCAB05_01.LocalReport.DataSources.Add(sCAB05_01);                

                if (header.PrintPreview)
                {
                    this.rptCAB05_01.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.rptCAB05_01.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }
            }
            catch (Exception ex)
            {
                throw;
            }
        }
    }
}

