using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using System.Data;
using PEA.BPM.AgencyManagementModule.DataSet;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using Microsoft.Reporting.WinForms;
using System.Collections.Generic;
using PEA.BPM.Architecture.PrintUtilities;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule.ReportViews
{
    [SmartPart]
    public partial class CAB06_01ReportView : UserControl, ICAB06_01ReportView
    {
        public CAB06_01ReportView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAB06_01ReportViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }


        public void ShowReport(CAB06_01HeaderReportInfo _myMaster, List<CAB06_01DetailReportInfo> _myDetail)
        {
            try
            {               
                ReportParameter[] ps = new ReportParameter[11];
                ps[0] = new ReportParameter("pPrintDate", _myMaster.PrintDate);                                                               
                ps[1] = new ReportParameter("pPeriodStartMonth", _myMaster.PeriodStartMonth);
                ps[2] = new ReportParameter("pPeriodEndMonth", _myMaster.PeriodEndMonth);
                ps[3] = new ReportParameter("pPeriodYear", _myMaster.PeriodYear);
                ps[4] = new ReportParameter("pStartBranchId", _myMaster.StartBranchId);
                ps[5] = new ReportParameter("pStartBranchName", _myMaster.StartBranchName);
                ps[6] = new ReportParameter("pEndBranchId", _myMaster.EndBranchId);
                ps[7] = new ReportParameter("pEndBranchName", _myMaster.EndBranchName);
                ps[8] = new ReportParameter("pTotalBillCollected", _myMaster.TotalBillCollected.Value.ToString());
                ps[9] = new ReportParameter("pTotalCommission", _myMaster.TotalCommission.Value.ToString("#,###.00"));
                ps[10] = new ReportParameter("pBranchName", _myMaster.BranchName);
             
                this.evaluateReportView.Reset();
                this.evaluateReportView.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                this.evaluateReportView.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAB06_01.rdlc";
               
                evaluateReportView.LocalReport.SetParameters(ps);               

                ReportDataSource sEvaluateDetailReport = new ReportDataSource();
                sEvaluateDetailReport.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_CAB06_01DetailReportInfo";
                sEvaluateDetailReport.Value = _myDetail;
                evaluateReportView.LocalReport.DataSources.Add(sEvaluateDetailReport);

                if (_myMaster.PrintPreview)
                {
                    this.evaluateReportView.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.evaluateReportView.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }
            }
            catch (Exception ex)
            {
                throw;
            }
        }

    }
}

