using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using System.Collections.Generic;
using System.Data;
using PEA.BPM.AgencyManagementModule.DataSet;
using Microsoft.Reporting.WinForms;
using System.Globalization;
using PEA.BPM.Architecture.CommonUtilities;
using PEA.BPM.Architecture.PrintUtilities;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule.ReportViews
{
    [SmartPart]
    public partial class CAB07_01ReportView : UserControl, ICAB07_01ReportView
    {
        private DateTimeFormatInfo _th_dt;        

        public CAB07_01ReportView()
        {
            InitializeComponent();
            CultureInfo th_culture = new CultureInfo("th-TH");
            _th_dt = th_culture.DateTimeFormat;
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAB07_01ReportViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }

        public void ShowReport(KeepMoneyPlaneHeaderInfoReport _myMaster, List<CAB07_01DetailReportInfo> _myDetail)
        {
            try
            {                
                dsKeepMoneyPlane ds = new dsKeepMoneyPlane();               
                // Header
                ReportParameter[] ps = new ReportParameter[3];
                ps[0] = new ReportParameter("PrintDate", _myMaster.PrintDate);
                ps[1] = new ReportParameter("BranchName", String.Format("{0}, {1}", _myMaster.BranchMasterName, _myMaster.BranchMasterId));
                string year = _myMaster.Period.Substring(0, 4);
                int _monthNo = Convert.ToInt16(_myMaster.Period.Substring(4, 2));
                ps[2] = new ReportParameter("Period", String.Format(" {0} {1}", StringConvert.GetMonthName(_monthNo), year));
              
                this.rev701460ReportView.Reset();
                this.rev701460ReportView.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                this.rev701460ReportView.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAB07_01.rdlc";
                rev701460ReportView.LocalReport.SetParameters(ps);               

                ReportDataSource sRev701460Report = new ReportDataSource();
                sRev701460Report.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_CAB07_01DetailReportInfo";
                sRev701460Report.Value = _myDetail;
                rev701460ReportView.LocalReport.DataSources.Add(sRev701460Report);

                if (_myMaster.PrintPreview)
                {
                    this.rev701460ReportView.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.rev701460ReportView.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }
                               
            }
            catch (Exception ex)
            {
                throw;
            }
        }

    }
}

