using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using System.Collections.Generic;
using Microsoft.Reporting.WinForms;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.Architecture.PrintUtilities;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule
{
    [SmartPart]
    public partial class CAB12_01ReportView : UserControl, ICAB12_01ReportView
    {
        public CAB12_01ReportView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAB12_01ReportViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }

        public void ShowReport(CAB12_01HeaderReportInfo header, List<CAB12_01DetailReportInfo> detail, List<CAB12_02DetailReportInfo> summary)
        {
            try
            {

                ReportParameter[] ps = new ReportParameter[6];
              
                ps[0] = new ReportParameter("pPrintDate", header.PrintDate);
                ps[1] = new ReportParameter("pBranchName", header.BranchName);
                ps[2] = new ReportParameter("pPast1_3Month", header.Past1_3Month.ToString());
                ps[3] = new ReportParameter("pPast4_6Month", header.Past4_6Month.ToString());
                ps[4] = new ReportParameter("pPast7_12Month", header.Past7_12Month.ToString());
                ps[5] = new ReportParameter("pPast1Year", header.Past1Year.ToString());

                ReportDataSource sCAB12_01 = new ReportDataSource();
                this.rptCAB12_01.Reset();
                this.rptCAB12_01.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                this.rptCAB12_01.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAB12_01.rdlc";
                sCAB12_01.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_CAB12_01DetailReportInfo";
                sCAB12_01.Value = detail;                
                rptCAB12_01.LocalReport.DataSources.Add(sCAB12_01);

                sCAB12_01 = new ReportDataSource();
                sCAB12_01.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_CAB12_02DetailReportInfo";
                sCAB12_01.Value = summary;
                rptCAB12_01.LocalReport.DataSources.Add(sCAB12_01);

                
                rptCAB12_01.LocalReport.SetParameters(ps);


                if (header.PrintPreview)
                {
                    this.rptCAB12_01.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.rptCAB12_01.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }
            }
            catch (Exception ex)
            {
                throw;
            }
        }
    }
}

