using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.Architecture.CommonUtilities;
using System.Globalization;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;

namespace PEA.BPM.AgencyManagementModule
{
    [SmartPart]
    public partial class CAN34_01ReportPopupView : UserControl, ICAN34_01ReportPopupView
    {
        private DateTimeFormatInfo _th_dt;

        public CAN34_01ReportPopupView()
        {
            InitializeComponent();                                               
        }

        public string SetAgencyId
        {
            set
            {
                agencyIdTb.Text = value;
                previewCb.Focus();
            }
        }


        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAN34_01ReportPopupViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
            CultureInfo th_culture = new CultureInfo("th-TH");
            _th_dt = th_culture.DateTimeFormat;
            SetDefaultView();                       
        }

        private void printbt_Click(object sender, EventArgs e)
        {
            if (IsFormValide())
            {
                CAN34_01CondtionReportInfo conn = new CAN34_01CondtionReportInfo();
                conn.AgencyId = agencyIdTb.Text.Trim() == String.Empty ? String.Empty : agencyIdTb.Text.Trim().PadLeft(12,'0');
                conn.BranchId = Session.Branch.Id;
                conn.Period = DaHelper.SetBillPeriod(periodTb.Text);
                conn.PrintPreview = previewCb.Checked;

                _presenter.ShowCAN34_01Clicked(conn);
            }
        }

           

        private void Closebt_Click(object sender, EventArgs e)
        {
            this.ParentForm.Close();
        }

        private void findAgencyIdBt_Click(object sender, EventArgs e)
        {
            _presenter.AgencySearchShowPopupClicked(1);
        }

        private void previewCb_KeyDown(object sender, KeyEventArgs e)
        {
            if (e.KeyCode == Keys.Enter)
            {
                previewCb.Focus();
            }
            else if (e.KeyCode == Keys.N)
            {
                string p = Session.BpmDateTime.Now.ToString("yyyyMMdd", _th_dt);
                string curPer = string.Format("{0}/{1}", p.Substring(4, 2), p.Substring(0, 4));
                periodTb.Text = curPer;

                previewCb.Focus();
            }
        }

        private void printAllrb_Click(object sender, EventArgs e)
        {
            if (printAllrb.Checked)
            {
                findAgecyIdBt.Enabled = false;
                agencyIdTb.Clear();
            }
        }

        private void printByAgencyrb_Click(object sender, EventArgs e)
        {
            if (printByAgencyrb.Checked)
            {
                findAgecyIdBt.Enabled = true;
            }
        }


        private bool IsFormValide()
        {
            bool retVal = false;
            if ((printByAgencyrb.Checked) && (agencyIdTb.Text == String.Empty))
            {
             
                MessageBox.Show("กรุณาระบุรหัสตัวแทนเก็บเงินเพื่อออกรายงาน", "ข้อผิดพลาด", MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
            else if (periodTb.Text.Trim() == string.Empty || periodTb.Text.Trim().Length != ModuleConfigurationNames.PeriodUseLength)
            {
                MessageBox.Show("ช่วงบิลเดือนสุดท้ายไม่ถูกต้อง", "ข้อผิดพลาด", MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
            else
            {
                retVal = true;
            }
            return retVal;
        }

        private void findAgecyIdBt_Click(object sender, EventArgs e)
        {
            _presenter.AgencySearchShowPopupClicked(1);
        }

        private void SetDefaultView()
        {
            agencyIdTb.Clear();                     
            agencyIdTb.Enabled = false;
            findAgecyIdBt.Enabled = false;            
            previewCb.Checked = true;
            periodTb.Text = Session.BpmDateTime.Now.ToString("MM/yyyy", new CultureInfo("th-TH"));
        }
    }
}

