using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using System.Collections.Generic;
using Microsoft.Reporting.WinForms;
using PEA.BPM.Architecture.PrintUtilities;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule
{
    [SmartPart]
    public partial class CAN34_01ReportView : UserControl, ICAN34_01ReportView
    {
        public CAN34_01ReportView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAN34_01ReportViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }

        public void ShowReport(CAN34_01HeaderReportInfo myMaster, List<CAN34_01DetailReportInfo> myDetail)
        {
            try
            {
                ReportParameter[] ps = new ReportParameter[3];
                ps[0] = new ReportParameter("pPrintDate", myMaster.PrintDate);
                ps[1] = new ReportParameter("pBranchName", myMaster.BranchName);
                ps[2] = new ReportParameter("pPeriod", myMaster.Period);

                this.rptCAN34_01.Reset();
                this.rptCAN34_01.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                this.rptCAN34_01.LocalReport.ReportEmbeddedResource = "PEA.BPM.AgencyManagementModule.Reports.CAN34_01.rdlc";
                rptCAN34_01.LocalReport.SetParameters(ps);

                ReportDataSource sCAN34_01 = new ReportDataSource();
                sCAN34_01.Name = "PEA_BPM_AgencyManagementModule_Interface_BusinessEntities_CAN34_01DetailReportInfo";
                sCAN34_01.Value = myDetail;
                rptCAN34_01.LocalReport.DataSources.Add(sCAN34_01);

                if (myMaster.PrintPreview)
                {
                    this.rptCAN34_01.RefreshReport();
                }
                else
                {
                    PrintUtility _printer = new PrintUtility(LocalSettingNames.AgencyPrinterName, ModuleConfigurationNames.ReportWidth, ModuleConfigurationNames.ReportHeight);
                    _printer.Export(this.rptCAN34_01.LocalReport);
                    _printer.CurrentPageIndex = 0;
                    _printer.Print();
                }
            }
            catch (Exception ex)
            {
                throw;
            }
        }
    }
}

