using System;
using System.Collections;
using System.Windows.Forms;

using Microsoft.Practices.CompositeUI.EventBroker;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;

using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using PEA.BPM.AgencyManagementModule.Interface.Services;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.AgencyManagementModule.Interface.Constants;
using PEA.BPM.Architecture.CommonUtilities;
using PEA.BPM.Architecture.ArchitectureTool.ErrorHandling;

namespace PEA.BPM.AgencyManagementModule
{
    public class BillBookPaymentStatusViewPresenter : Presenter<IBillBookPaymentStatusView>
    {
        IBillbookCheckInService _billBookCheckInService;
        IAgencyCommonService _agencyCommonService;

        [InjectionConstructor]
        public BillBookPaymentStatusViewPresenter([ServiceDependency] IBillbookCheckInService billBookCheckInService, 
                IAgencyCommonService agencyCommonService)
		{
            _billBookCheckInService = billBookCheckInService;
            _agencyCommonService = agencyCommonService;
		}


        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
            ((UserControl)View).ParentForm.Text = "สถานะการชำระเงิน";
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }

        public HashInfoCollection GetPmList(string pmId)
        {
            return _agencyCommonService.GetPmList(pmId);
        }

        public BillBookCheckInInfo BillBookIdTextChangeCommitted(string billBookId)
        {
            BillBookCheckInInfo billbook = new BillBookCheckInInfo();
            try
            {
                string newBillBookId = billBookId.Replace("-", "");               
                WaitingFormHelper.ShowWaitingForm();
                billbook = _billBookCheckInService.GetBookCheckInHistory(newBillBookId);
                WaitingFormHelper.HideWaitingForm();
               
            }
            catch (Exception ex)
            {
                WaitingFormHelper.HideWaitingForm();
                Logger.WriteError(Logger.Module.AGENCY, "ตรวจสอบสถานะสมุดจ่ายบิล", ex.ToString());
                ClientExceptionController.ShowExceptionDialog(EErrorInModule.Agency, ex);
            }
            return billbook;
        }

        public BillBookCheckInInfo GroupInvoiceTextChangeComitted(string billBookId, string branchId)
        {
            BillBookCheckInInfo billbook = new BillBookCheckInInfo();
            try
            {                
                WaitingFormHelper.ShowWaitingForm();
                billbook = _billBookCheckInService.GetGroupInvoiceCheckInHistory(billBookId, branchId);
                WaitingFormHelper.HideWaitingForm();

            }
            catch (Exception ex)
            {
                WaitingFormHelper.HideWaitingForm();
                Logger.WriteError(Logger.Module.AGENCY, "ตรวจสอบสถานะสมุดจ่ายบิล", ex.ToString());
                ClientExceptionController.ShowExceptionDialog(EErrorInModule.Agency, ex);
            }
            return billbook;
        }

    }
}

