using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.AgencyManagementModule.Interface.BusinessEntities;
using PEA.BPM.AgencyManagementModule.Interface.Constants;

namespace PEA.BPM.AgencyManagementModule
{
    public class FineDetailViewPresenter : Presenter<IFineDetailView>
    {
        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }

        [EventPublication(EventTopicNames.LoadFineDetail, PublicationScope.Global)]
        public event EventHandler<EventArgs> LoadFineDetailHandler;
        public void ShowFineDetail()
        {
            if (LoadFineDetailHandler != null)
            {
                WorkItem.State["LoadFineDetail"] = true;
                LoadFineDetailHandler(this, new EventArgs());
            }
        }

        [EventSubscription(EventTopicNames.FillFineDetail, Thread = ThreadOption.UserInterface)]
        public void FillFineDetailHandler(object sender, EventArgs<FineDetailInfo> e)
        {
            if (WorkItem.State["FillFineDetail"] != null)
            {
                View.FileDetail = e.Data;
                WorkItem.State["FillFineDetail"] = null;
            }
        }


        [EventPublication(EventTopicNames.DisableFineEvent, PublicationScope.Global)]
        public event EventHandler<EventArgs> DisableFineEventHandler;
        public void CancelFine()
        {
            if (DisableFineEventHandler != null)
                DisableFineEventHandler(this, new EventArgs());
        }

    }
}

