using System;
using System.Windows.Forms;
using System.Drawing;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Architecture.ArchitectureTool.ErrorHandling;
using PEA.BPM.Infrastructure.Interface;
using System.Collections.Generic;
using PEA.BPM.BillPrintingModule.Interface.BusinessEntities;
using System.ComponentModel;
using PEA.BPM.BillPrintingModule.BillPrintingUtilities;
using PEA.BPM.Architecture.CommonUtilities;

namespace PEA.BPM.BillPrintingModule
{
    [SmartPart]
    public partial class DeliveryPlaceView : UserControl, IDeliveryPlaceView
    {
        #region "Variables & Properties"

        string _tmpId;
        string _flag = "Insert";
        List<DeliveryPlace> _deliveryPlace;

        public List<DeliveryPlace> DeliveryPlace
        {
            get { return _deliveryPlace; }
            set { _deliveryPlace = value; }
        }

        #endregion

        #region "Code Generated"

        public DeliveryPlaceView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public DeliveryPlaceViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
            InitializeDataGridView();
        }

        #endregion

        #region "Event Handling"

        private void submitButton_Click(object sender, EventArgs e)
        {
            try
            {
                if (_flag != "Delete")
                    if (branchIdTextBox.Text != string.Empty ||
                    deliveryPlaceTextBox.Text != string.Empty)
                        DoUpdate();
                    else
                        MessageBox.Show("กรุณากรอกข้อมูล", MessageBoxText.CaptionWarning, MessageBoxButtons.OK, MessageBoxIcon.Exclamation);
                else
                    DoUpdate();

                ClearControlValue();
            }
            catch (Exception ex)
            {
                ClientExceptionController.ShowExceptionDialog(EErrorInModule.BLAN, ex);
                //MessageBox.Show("เกิดข้อผิดพลาดภายในระบบ :" + ex.ToString() , MessageBoxText.CaptionWarning, MessageBoxButtons.OK, MessageBoxIcon.Exclamation);
            }
        }     

        private void cancelButton_Click(object sender, EventArgs e)
        {
            ClearControlValue();
        }
       
        private void deliveryPlaceDataGridView_CellContentClick(object sender, DataGridViewCellEventArgs e)
        {
            _tmpId = deliveryPlaceDataGridView.Rows[e.RowIndex].Cells["Id"].Value.ToString();

            if (deliveryPlaceDataGridView.Columns[e.ColumnIndex].Name == "EditColumn")
            {
                branchIdTextBox.Text = deliveryPlaceDataGridView.Rows[e.RowIndex].Cells["DeliveryBranchId"].Value.ToString();
                deliveryPlaceTextBox.Text = deliveryPlaceDataGridView.Rows[e.RowIndex].Cells["PlaceName"].Value.ToString();
                branchIdTextBox.Focus();
                submitButton.Text = "แก้ไข";
                _flag = "Edit";  
            }
            else if (deliveryPlaceDataGridView.Columns[e.ColumnIndex].Name == "DeleteColumn")
            {
                DialogResult dr = MessageBox.Show("กรุณากด Yes เพื่อยืนยันการลบ", MessageBoxText.CaptionConfirm, MessageBoxButtons.YesNo, MessageBoxIcon.Question);
                if ( dr == DialogResult.Yes)
                {
                    _flag = "Delete";
                    DoUpdate();
                }
            }

            //InitializeDataGridView();
        }

        #endregion

        #region "Function"

        private void ClearControlValue()
        {
            submitButton.Text = "เพิ่ม";
            _flag = "Insert";
            deliveryPlaceTextBox.Text = string.Empty;
            branchIdTextBox.Text = string.Empty;
            branchIdTextBox.Focus();

            InitializeDataGridView();
        }

        private void DoUpdate()
        {
            DeliveryPlace dp = new DeliveryPlace();
            dp.Id = _tmpId;
            dp.DeliveryBranchId = branchIdTextBox.Text;
            dp.PlaceName = deliveryPlaceTextBox.Text;
            dp.CreateBranchId = Session.Branch.Id;            
            dp.ModifiedBy = Session.User.Id;
            
            if (_flag == "Edit")
            {
                _presenter.ModifyDeliveryPlace(dp, "Edit");
                branchIdTextBox.Text = string.Empty;
                deliveryPlaceTextBox.Text = string.Empty;
                deliveryPlaceTextBox.Focus();
                //MessageBox.Show(MessageBoxText.MsgProcessingDone, MessageBoxText.CaptionInfo, MessageBoxButtons.OK, MessageBoxIcon.Information);
            }
            else if (_flag == "Insert")
            {
                _presenter.ModifyDeliveryPlace(dp, "Insert");
                branchIdTextBox.Text = string.Empty;
                deliveryPlaceTextBox.Text = string.Empty;
                deliveryPlaceTextBox.Focus();
                //MessageBox.Show(MessageBoxText.MsgProcessingDone, MessageBoxText.CaptionInfo, MessageBoxButtons.OK, MessageBoxIcon.Information);
            }
            else if (_flag == "Delete")
            {
                _presenter.ModifyDeliveryPlace(dp, "Delete");
                //MessageBox.Show(MessageBoxText.MsgProcessingDone, MessageBoxText.CaptionInfo, MessageBoxButtons.OK, MessageBoxIcon.Information);
            }

            
            
            InitializeDataGridView();          
        }
        
        private void InitializeDataGridView()
        {
            BindingList<DeliveryPlace> bs = new BindingList<DeliveryPlace>(_presenter.LoadDeliveryPlace(Session.Branch.Id));
            deliveryPlaceDataGridView.Refresh();
            deliveryPlaceDataGridView.AutoGenerateColumns = false;
            deliveryPlaceDataGridView.DataSource = bs;
        }
      
        #endregion

        private void branchIdTextBox_KeyPress(object sender, KeyPressEventArgs e)
        {
            if (e.KeyChar == (char)Keys.Enter)
                if (branchIdTextBox.Text != string.Empty)
                    deliveryPlaceTextBox.Focus();
        }

        private void deliveryPlaceTextBox_KeyPress(object sender, KeyPressEventArgs e)
        {
            if (e.KeyChar == (char)Keys.Enter)
                if (deliveryPlaceTextBox.Text != string.Empty && branchIdTextBox.Text != string.Empty)
                    submitButton.Focus();
        }

        private void closeBt_Click(object sender, EventArgs e)
        {
            _presenter.DeliveryToUpdatedHandler();
            //_presenter.OnCloseView();
        }
    }
}

