using System;
using System.Windows.Forms;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.Architecture.ArchitectureTool.ErrorHandling;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.Architecture.CommonUtilities;
using PEA.BPM.BillPrintingModule.Constants;
using PEA.BPM.BillPrintingModule.Interface.BusinessEntities;
using PEA.BPM.BillPrintingModule.Interface.Services;
using System.Collections.Generic;
using PEA.BPM.BillPrintingModule.BillPrintingUtilities;

namespace PEA.BPM.BillPrintingModule
{
    public class DeliveryPlaceViewPresenter : Presenter<IDeliveryPlaceView>
    {
        private IControlServices _controlServices;

        [InjectionConstructor]
        public DeliveryPlaceViewPresenter([ServiceDependency] IControlServices controlServices)
        {
            _controlServices = controlServices;
        }

        [EventPublication(EventTopicNames.DeliveryToUpdated, PublicationScope.Global)]
        public event EventHandler DeliveryToUpdated;
        public void DeliveryToUpdatedHandler()
        {
            if (DeliveryToUpdated != null)
                DeliveryToUpdated(this, new EventArgs());
        }

        public List<DeliveryPlace> LoadDeliveryPlace(string createBranchId)
        {
            return _controlServices.GetDeliveryPlace(createBranchId);
        }

        public string ModifyDeliveryPlace(DeliveryPlace param, string flag)
        {
            string row = "";
            try
            {
                if (flag == "Insert")
                    row = _controlServices.InsertDeliveryPlace(null, param);
                else if (flag == "Edit")
                    row = _controlServices.UpdateDeliveryPlace(null, param);
                else if (flag == "Delete")
                    row = _controlServices.DeleteDeliveryPlace(null, param);
            }
            catch (Exception ex)
            {
                ClientExceptionController.ShowExceptionDialog(EErrorInModule.BLAN, ex);
                //MessageBox.Show(MessageBoxText.MsgGeneralError + ex.Message, MessageBoxText.CaptionError, MessageBoxButtons.OK, MessageBoxIcon.Warning);
            }

            return row;
        }

        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }
    }
}

