using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.BillPrintingModule.Interface.Services;
using PEA.BPM.BillPrintingModule.Interface.BusinessEntities;
using PEA.BPM.BillPrintingModule.Constants;
using Microsoft.Practices.CompositeUI.EventBroker;
using System.Collections.Generic;

namespace PEA.BPM.BillPrintingModule
{
    public class GreenBillViewPresenter : Presenter<IDirectDebitBillView>
    {
        private IControlServices _controlServices;
        private IBillPrintingServices _printingServices;
        
        [InjectionConstructor]
        public GreenBillViewPresenter([ServiceDependency] IControlServices controlServices, [ServiceDependency] IBillPrintingServices printingServices)
		{
            _controlServices = controlServices;
            _printingServices = printingServices;
		}

        public void LoadBankComboBox(string branchId)
        {
            View.Bank = _controlServices.GetBank(branchId);
        }

        public void CheckExistByBank(GreenBillParam param)
        {
            //syncronous call
            View.BillByBank = _printingServices.CheckExistByBank(param);
        }

        [EventPublication(EventTopicNames.PrintGreenBill, PublicationScope.WorkItem)]
        public event EventHandler<EventArgs<List<PrintableIdByBank>>> PrintGreenBill;
        public void PrintGreenBillByBank(List<PrintableIdByBank> param)
        {
            if (PrintGreenBill != null)
                PrintGreenBill(this, new EventArgs<List<PrintableIdByBank>>(param));
        }

        [EventSubscription(EventTopicNames.WaitCursor, Thread = ThreadOption.UserInterface)]
        public void ShowWaitCursor(object sender, EventArgs<bool> e)
        {
            View.OnWaitCursor(e.Data);
        }

        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }


    }
}

