        private void Print()
        {
            if (ValidateBeforeSubmit())
            {
                    this.Cursor = Cursors.AppStarting;
                    int arraySize = dualBillReprintDataGridView.Rows.Count;
                    string[] groupElectricId = new string[arraySize];
                    string[] groupFromToElectricId = new string[arraySize];
                    string[] groupFromToNumberId = new string[arraySize];

                    BillPrintingConditionParam param = new BillPrintingConditionParam();
                    param.CommBranchId = Session.Branch.Id;
                    param.PrintedBy = Session.User.Id;
                    param.IsReprintBill = true;
                    param.BranchId = Session.Branch.Id;

                    if (!printBillSeqRadioButton.Checked)
                        param.BillPeriod = billPeriodMaskedTextBox.Text.Substring(3, 4) + billPeriodMaskedTextBox.Text.Substring(0, 2);

                    if (greenBillRadioButton.Checked)
                        param.BillType = (int)BillType.ReprintGreenBill;
                    else if (blueBillRadioButton.Checked)
                        param.BillType = (int)BillType.ReprintBlueBill;
                    else if (spotbillRadioButton.Checked)
                        param.BillType = (int)BillType.ReprintSpotBill;
                    else
                        throw new Exception("Wrong bill type.");

                    if (normalBillRb.Checked)
                        param.HasOrgDoc = 0;
                    else
                        param.HasOrgDoc = 1;

                    OverlapHelper oh = new OverlapHelper();

                    if (printMruRadioButton.Checked == true)
                    {
                        param.BillPrintingCondition = (int)PrintingCondition.MruPrinting;

                        if (dualBillReprintDataGridView.Rows.Count != 0)
                        {
                            for (int i = 0; i < dualBillReprintDataGridView.Rows.Count; i++)
                            {
                                groupElectricId[i] = dualBillReprintDataGridView.Rows[i].Cells["electricityIdColumn"].Value.ToString();
                                oh.Check(groupElectricId[i]);
                            }
                            param.GroupElectricId = oh.GetResult().ToArray();
                        }
                    }
                    else if (printUserRadioButton.Checked == true)
                    {
                        param.BillPrintingCondition = (int)PrintingCondition.UserPrinting;
                        if (dualBillReprintDataGridView.Rows.Count != 0)
                        {
                            for (int i = 0; i < dualBillReprintDataGridView.Rows.Count; i++)
                            {
                                //From UserId - To User Id
                                groupFromToElectricId[i] = dualBillReprintDataGridView.Rows[i].Cells["fullIdColumn"].Value.ToString();
                                oh.Check(groupFromToElectricId[i]);
                            }
                            param.GroupFromToElectricId = oh.GetResult().ToArray();
                        }
                    }
                    else if (printBillSeqRadioButton.Checked == true)
                    {
                        param.BillPrintingCondition = (int)PrintingCondition.BillSeqPrinting;
                        if (dualBillReprintDataGridView.Rows.Count != 0)
                        {
                            for (int i = 0; i < dualBillReprintDataGridView.Rows.Count; i++)
                            {
                                //From Bill Seq Number - To Bill Seq Number
                                groupFromToNumberId[i] = dualBillReprintDataGridView.Rows[i].Cells["billSeqColumn"].Value.ToString();
                                oh.Check(groupFromToNumberId[i]);
                            }
                            param.GroupFromToNumberId = oh.GetResult().ToArray();
                        }
                    }

                    _presenter.ReprintDualBillAndReceipt(param);

                    this.Cursor = Cursors.Default;
                //}
            }
        }

        private void printButton_Click(object sender, EventArgs e)
        {
            if (!_isReadyToPrint)
            {
                Print();
            }
            else
            {
                DialogResult dr = MessageBox.Show(MessageBoxText.MsgConfirmPrintingBill, MessageBoxText.CaptionConfirm, MessageBoxButtons.YesNo, MessageBoxIcon.Question);
                if (dr == DialogResult.Yes)
                {
                    if (Authorization.IsAuthorized(SecurityNames.DualBillReprinting, true))
                        _presenter.PrintSelectedBillHandler();
                }
            }
        }

        private void clearButton_Click(object sender, EventArgs e)
