        private void ShowGirdViewColumn()
        {
            if (printMruRadioButton.Checked == true)
            {
                dualBillReprintDataGridView.Columns["electricityIdColumn"].AutoSizeMode = DataGridViewAutoSizeColumnMode.Fill;
                dualBillReprintDataGridView.Columns["electricityIdColumn"].Visible = true;
                dualBillReprintDataGridView.Columns["fromNumberColumn"].Visible = false;
                dualBillReprintDataGridView.Columns["toNumberColumn"].Visible = false;
            }

            if (printBillSeqRadioButton.Checked == true)
            {
                dualBillReprintDataGridView.Columns["electricityIdColumn"].Visible = false;
                dualBillReprintDataGridView.Columns["fromNumberColumn"].Visible = true;
                dualBillReprintDataGridView.Columns["toNumberColumn"].Visible = true;
            }

            if (printUserRadioButton.Checked == true)
            {
                //dualBillReprintDataGridView.Columns["electricityIdColumn"].AutoSizeMode = DataGridViewAutoSizeColumnMode.DisplayedCells;
                dualBillReprintDataGridView.Columns["electricityIdColumn"].Visible = false;
                dualBillReprintDataGridView.Columns["fromNumberColumn"].Visible = true;
                dualBillReprintDataGridView.Columns["toNumberColumn"].Visible = true;
            }
        }

        private void AddToDataGridView(string txtId, string txtFromId, string txtToId)
        {
            bool isRepeatedItem = false;
            string dash = Properties.Resources.PrintSeperatorText;
            dualBillReprintDataGridView.Enabled = true;

            if (printMruRadioButton.Checked == true)
                isRepeatedItem = CheckRepeatedItem(txtId, isRepeatedItem);

            if (isRepeatedItem == false)
            {
                try
                {
                    int i = dualBillReprintDataGridView.Rows.Count;
                    dualBillReprintDataGridView.Rows.Add(1);
                    dualBillReprintDataGridView.Rows[i].Selected = true;
                    if (printMruRadioButton.Checked == true)
                    {
                        dualBillReprintDataGridView.Rows[i].Cells["electricityIdColumn"].Value = txtId;
                    }

                    if (printUserRadioButton.Checked == true)
                    {

                        if (txtToId == string.Empty)
                            txtToId = txtFromId;

                        dualBillReprintDataGridView.Rows[i].Cells["fromNumberColumn"].Value = txtFromId;
                        dualBillReprintDataGridView.Rows[i].Cells["toNumberColumn"].Value = txtToId;

                        //insert into column "fullId", visible = false
                        dualBillReprintDataGridView.Rows[i].Cells["fullIdColumn"].Value = "UserId" + dash + txtFromId + dash + txtToId;
                    }

                    if (printBillSeqRadioButton.Checked == true)
                    {
                        if (txtToId == string.Empty)
                            txtToId = txtFromId;

                        dualBillReprintDataGridView.Rows[i].Cells["fromNumberColumn"].Value = txtFromId;
                        dualBillReprintDataGridView.Rows[i].Cells["toNumberColumn"].Value = txtToId;
                        dualBillReprintDataGridView.Rows[i].Cells["billSeqColumn"].Value = "BillSeq" + dash + txtFromId + dash + txtToId;
                    }

                    //when selected = true, bgColor = blue so we didn't need it.
                    for (int j = 0; j <= i - 1; j++)
                    {
                        dualBillReprintDataGridView.Rows[j].Selected = false;
                    }

                }
                catch (Exception ex)
                {
                    ClientExceptionController.ShowExceptionDialog(EErrorInModule.BLAN, ex);
                    //MessageBox.Show(MessageBoxText.MsgGeneralError + ex.Message, MessageBoxText.CaptionError, MessageBoxButtons.OK, MessageBoxIcon.Warning);
                }
            }
        }

        private bool CheckRepeatedItem(string txtId, bool isRepeated)
        {
            if (printMruRadioButton.Checked == true)
            {
                if (dualBillReprintDataGridView.Rows.Count != 0)
                {
                    for (int i = 0; i < dualBillReprintDataGridView.Rows.Count; i++)
                    {

                        if (txtId == (string)dualBillReprintDataGridView.Rows[i].Cells["electricityIdColumn"].Value)
                        {
                            isRepeated = true;
                            break;
                        }
                        else
                            isRepeated = false;
                    }
                }
            }
            return isRepeated;
        }

