        private void SetState(SelectionState state)
        {
            if (state == SelectionState.All)
                EnabledPrintAll(true);
            else if (state == SelectionState.Branch)
                EnabledPrintByBranch(true);
            else if (state == SelectionState.MRU)
                EnabledPrintByMRU(true);
            else if (state == SelectionState.CA)
                EnabledPrintByCA(true);
            else
                throw new Exception("Selection state error!");

            _selectionState = state;
        }


        private void OnSelectionStateChanged(SelectionState state, bool enabledGv)
        {
            if (dualBillDataGridView.Rows.Count != 0)
            {
                if (_selectionState != state)
                {
                    DialogResult dr = MessageBox.Show(MessageBoxText.MsgConfirmConditionChanged, MessageBoxText.CaptionConfirm, MessageBoxButtons.YesNo, MessageBoxIcon.Question);
                    if (dr == DialogResult.Yes)
                    {
                        dualBillDataGridView.Enabled = enabledGv;
                        dualBillDataGridView.Rows.Clear();
                    }
                    else //same state
                    {
                        SetState(_selectionState);
                        return;
                    }
                }
            }

            SetState(state);            
        }


