using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.BillPrintingModule.Constants;
using PEA.BPM.BillPrintingModule.Interface.BusinessEntities;
using PEA.BPM.BillPrintingModule.Interface.Services;

namespace PEA.BPM.BillPrintingModule
{
    public class ReportBillStatusViewPresenter : Presenter<IReportBillStatusView>
    {
        private IControlServices _controlServices;
        
        [InjectionConstructor]
        public ReportBillStatusViewPresenter([ServiceDependency] IControlServices controlServices)
		{
            _controlServices = controlServices;
		}

        [EventPublication(EventTopicNames.PrintBillStatusReport, PublicationScope.WorkItem)]
        public event EventHandler<EventArgs<ReportConditionParam>> PrintBillStatusReportHandler;
        public void PreviewBillStatusReport(ReportConditionParam param)
        {
            if (PrintBillStatusReportHandler != null)
                PrintBillStatusReportHandler(this, new EventArgs<ReportConditionParam>(param));
        }

        [EventSubscription(EventTopicNames.WaitCursor, Thread = ThreadOption.UserInterface)]
        public void ShowWaitCursor(object sender, EventArgs<bool> e)
        {
            View.OnWaitCursor(e.Data);
        }

        [EventPublication(EventTopicNames.GetPortion, PublicationScope.WorkItem)]
        public event EventHandler<EventArgs<string>> GetPortionHandler;
        public void GetPortion(string branchId)
        {
            if (GetPortionHandler != null)
                GetPortionHandler(this, new EventArgs<string>(branchId));
        }

        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }

    }
}

