        private bool ValidateForm()
        {
            sapRefNoTxt.Text = sapRefNoTxt.Text.Replace(" ","").ToUpper();
            if (sapRefNoTxt.Text == string.Empty)
            {
                MessageBox.Show("กรุณากรอก " + RefNoLb.Text.Replace(":", ""), "ข้อผิดพลาด", MessageBoxButtons.OK, MessageBoxIcon.Exclamation);
                sapRefNoTxt.Focus();
                return false;
            }else if(sapRefNoTxt.Text.Length != sapRefNoTxt.MaxLength)
            {
                MessageBox.Show("กรุณากรอก " + RefNoLb.Text.Replace(":", "") +" ให้ครบถ้วน", "ข้อผิดพลาด", MessageBoxButtons.OK, MessageBoxIcon.Exclamation);
                sapRefNoTxt.Focus();
                return false;
            }

            if (paymentDataGridView.Rows.Count == 0)
            {
                MessageBox.Show("กรุณากรอกรายการเงินสดหรือรายการเช็ค", "ข้อผิดพลาด", MessageBoxButtons.OK, MessageBoxIcon.Exclamation);
                return false;
            }

            return true;
        }

        private bool ValidateAddInGridView()
