        private void okButton_Click(object sender, EventArgs e)
        {
            try
            {
                WorkStatus ci = IsClosedWork();
                if (ci.CloseWorkBy == null)
                {
                    if (ValidateForm())
                    {
                        //confirmation box
                        DialogResult dlg = MessageBox.Show("คุณต้องการนำเงินเข้าระบบ \nกรุณากดปุ่ม OK เพื่อยืนยันการนำเงินเข้าระบบ", "ยืนยันการนำเงินเข้า", MessageBoxButtons.OKCancel, MessageBoxIcon.Question);

                        if (dlg == DialogResult.OK)
                        {
                            if (_presenter.ExistSapRefNo(sapRefNoTxt.Text, Session.Work.Id))
                            {
                                MessageBox.Show("เลขที่อ้างอิงนี้ ได้ถูกใช้ในการรับเงินเข้าระบบแล้ว", "เลขที่อ้างอิงซ้ำ", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                                return;
                            }

                            MoneyCheckInInfo param = new MoneyCheckInInfo();
                            param.SAPRefNo = sapRefNoTxt.Text;
                            param.BranchId = Session.Branch.Id;
                            param.CashierId = Session.User.Id;
                            param.CashierName = Session.User.Name;
                            param.WorkId = Session.Work.Id;

                            if (paymentTypeTabControl.SelectedIndex == 0)
                                param.DtId = "MZ9800015"; //เงินสดจากการแลกเช็ค
                            else
                                param.DtId = "MZ9800025"; //ฝากเช็คจาก sap เข้าระบบ

                            param.PaymentDt = Session.BpmDateTime.Now;
                            param.Pending = "0";
                            param.PmId = "C";
                            param.PosId = Session.Terminal.Id;
                            param.TotalAmount = Convert.ToDecimal(totalAmtTxt.Text);

                            param.AdjAmount = Convert.ToDecimal(adjAmtTb.Text);
                            param.Amount = Convert.ToDecimal(amtTb.Text);

                            param.ModifiedBy = Session.User.Id;
                            
                            List<PaymentMethodInfo> list = new List<PaymentMethodInfo>();
                            for (int i = 0; i < paymentDataGridView.Rows.Count; i++)
                            {
                                PaymentMethodInfo pm = (PaymentMethodInfo)paymentDataGridView.Rows[i].DataBoundItem;

                                //Cash payment have only 1 record.
                                if (pm.PtId == "1")
                                {
                                    pm.Amount = Convert.ToDecimal(totalAmtTxt.Text);
                                    i = paymentDataGridView.Rows.Count;
                                }

                                list.Add(pm);
                            }

                            param.PaymentMethodList = list;
                            _presenter.CheckInMoney(param);

                            MessageBox.Show("รับเงินเข้าระบบเรียบร้อยแล้ว \nกรุณากดปุ่ม OK เพื่อทำงานต่อ", "สำเร็จ", MessageBoxButtons.OK, MessageBoxIcon.Information);
                            ClearScreen();
                        }
                    }
                }
                else
                {
                    NotifyMsg.ShowForceCloseWorkMsg(ci);
                    _presenter.OnCashierOpenWork("tmp");
                    _presenter.OnCloseView();
                }
            }
            catch (Exception ex)
            {
                MessageBox.Show(ex.ToString(), "ผิดพลาด", MessageBoxButtons.OK);
            }

        }

        private void ClearScreen()
        {
            sapRefNoTxt.Clear();
