            chequeActualAmountTextBox.Text = "0.00";
            chequeBankAccNoTextBox.Clear();
            chequeBankComboBox.Text = "";
            chequeDateDateTimePicker.ResetText();
            totalAmtTxt.Text = "0.00";
            amtTb.Text = "0.00";
            adjAmtTb.Text = "0.00";
            paymentTypeTabControl.SelectedIndex = 0;
            sapRefNoTxt.Focus();

        }

