        private void SetFormByPaymentType(int paymentId)
        {
            switch (paymentId)
            { 
                case 0: //Cash
                    RefNoLb.Text = "เลขที่ใบสำคัญจ่าย :";
                    exLb1.Text = "e.g. เลขที่ใบสำคัญจ่าย";
                    exLb2.Text = "G01320110214400001";
                    sapRefNoTxt.MaxLength = 18;
                    break;
                case 1: //Cheque
                    RefNoLb.Text = "เลขที่  LOT-B :";
                    exLb1.Text = "e.g. เลขที่ LOT-B";
                    exLb2.Text = "190090225B01";
                    sapRefNoTxt.MaxLength = 12;
                    break;
                default:
                    break;
            }
        }

        private void paymentTypeTabControl_SelectedIndexChanged(object sender, EventArgs e)
        {
            sapRefNoTxt.Clear();
            SetFormByPaymentType(paymentTypeTabControl.SelectedIndex);

            if (paymentTypeTabControl.SelectedIndex == 0) //cash
            {
                if (paymentDataGridView.Rows.Count > 0 && _paymentMethods[0].PtId == "2") //cheque
                {
                    DialogResult dlg = MessageBox.Show("คุณต้องการเปลี่ยนรูปแบบการนำเงินเข้าใช่หรือไม่\nกดปุ่ม OK เพื่อเปลี่ยนรูปแบบ", "กรุณายืนยัน", MessageBoxButtons.OKCancel, MessageBoxIcon.Warning);
                    if (dlg == DialogResult.OK)
                    {
                        paymentDataGridView.Rows.Clear();
                        cashAmountTextBox.Focus();
                        cashAmountTextBox.SelectAll();
                    }
                    else
                        paymentTypeTabControl.SelectedIndex = 1;
                }
                else
                {
                    cashAmountTextBox.Focus();
                    cashAmountTextBox.SelectAll();
                }
            }
            else if(paymentTypeTabControl.SelectedIndex == 1)//cheque 
            {
                if (paymentDataGridView.Rows.Count > 0 && _paymentMethods[0].PtId == "1") // cash
                {
                    DialogResult dlg = MessageBox.Show("คุณต้องการเปลี่ยนรูปแบบการนำเงินเข้าใช่หรือไม่\nกดปุ่ม OK เพื่อเปลี่ยนรูปแบบ", "กรุณายืนยัน", MessageBoxButtons.OKCancel, MessageBoxIcon.Warning);
                    if (dlg == DialogResult.OK)
                    {
                        paymentDataGridView.Rows.Clear();
                        chequeActualAmountTextBox.Focus();
                        chequeActualAmountTextBox.SelectAll();
                    }
                    else
                        paymentTypeTabControl.SelectedIndex = 0;
                }
                else
                {
                    chequeActualAmountTextBox.Focus();
                    chequeActualAmountTextBox.SelectAll();
                }
            }
        }

        private void chequeActualAmountTextBox_Leave(object sender, EventArgs e)
        {
            try
            {
                decimal temp = Convert.ToDecimal(chequeActualAmountTextBox.Text);
                if (temp == 0)
                    chequeActualAmountTextBox.Text = "0.00";
                else
                    chequeActualAmountTextBox.Text = temp.ToString("#,###.00");

            }
            catch (Exception ex)
            {
                DialogResult dlg = MessageBox.Show("กรุณาป้อนจำนวนเงินที่เป็นตัวเลขเท่านั้น", "ป้อนจำนวนเงินไม่ถูกต้อง", MessageBoxButtons.OKCancel, MessageBoxIcon.Warning);
                if (dlg == DialogResult.OK)
                {
                    chequeActualAmountTextBox.Text = "0.00";
                    chequeActualAmountTextBox.Focus();
                    chequeActualAmountTextBox.SelectAll();
                }
                else if (dlg == DialogResult.Cancel)
                {
                    chequeActualAmountTextBox.Text = _prvCashAmt;
                }
            }

        }

