using System;
using System.Windows.Forms;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.CashManagementModule.Interface.Services;
using PEA.BPM.CashManagementModule.Interface.BusinessEntities;
using PEA.BPM.CashManagementModule.Interface.Constants;
using PEA.BPM.Infrastructure.Interface.BusinessEntities;

namespace PEA.BPM.CashManagementModule
{
    public class OpenWorkItemViewPresenter : Presenter<IOpenWorkItemView>
    {
        private ICashManagementServices _services;
        private ICashReportServices _cashReportServices;

        public OpenWorkItemViewPresenter()
        {
        }

        [InjectionConstructor]
        public OpenWorkItemViewPresenter([ServiceDependency] ICashManagementServices services,
                                                             ICashReportServices cashReportServices)
        {
            _services = services;
            _cashReportServices = cashReportServices;
        }

        public string OpenWork(OpenWorkParam param)
        {
            return _services.OpenWork(param);
        }

        public void LoadOpeningBalance(string cashierId, string flowType)
        {
            View.OpeningBalance = _services.LoadOpeningBalance(cashierId, flowType);
        }

        [EventPublication(EventTopicNames.StartWork, PublicationScope.Global)]
        public event EventHandler<EventArgs> StartWorkHandler;
        public void Start()
        {
            if (StartWorkHandler != null)
                StartWorkHandler(this, new EventArgs());
        }

        [EventPublication(EventTopicNames.CashierOpenWork, PublicationScope.Global)]
        public event EventHandler<EventArgs<string>> CashierOpenWorkHandler;
        public void OnCashierOpenWork(string tmp)
        {
            if (CashierOpenWorkHandler != null)
                CashierOpenWorkHandler(this, new EventArgs<string>(tmp));
        }

        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }
    }
}

