        public void Preview(List<ReportDailyRemainInfo> reportData)
        {
            try
            {
                rdlcViewer.Reset();
                rdlcViewer.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                rdlcViewer.LocalReport.ReportEmbeddedResource = "PEA.BPM.CashManagementModule.Reports.DailyRemainReport.rdlc";

                if (reportData.Count > 0)
                {
                    ReportDataSource sReportDataSource = new ReportDataSource();
                    sReportDataSource.Name = "PEA_BPM_CashManagementModule_Interface_BusinessEntities_ReportDailyRemainInfo";
                    sReportDataSource.Value = reportData;
                    rdlcViewer.LocalReport.DataSources.Add(sReportDataSource);
                }
                else
                {
                    //tell user and just quite
                    MessageBox.Show("ไม่พบข้อมูลสำหรับออกรายงาน กรุณาป้อนข้อมูลใหม่", "คำเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                    return;
                }

                //cash amount as parameter
                ReportParameter[] rParam = new ReportParameter[2];
                rParam[0] = new ReportParameter("parBranchName", Session.Branch.Name);
                rParam[1] = new ReportParameter("parCashierName", Session.User.Name);
                rdlcViewer.LocalReport.SetParameters(rParam);
                rdlcViewer.SetDisplayMode(DisplayMode.PrintLayout);
                rdlcViewer.ZoomMode = Microsoft.Reporting.WinForms.ZoomMode.PageWidth;
                //rdlcViewer.RefreshReport();               
            }
            catch (Exception e)
            {
                this.Cursor = Cursors.Default;
                ServiceHelper.TransformErrorMessage(e);
            }
        }

        public void Preview(ReportWorkFlowSummary reportData, string reportCode)
        //public void Preview(ReportWorkFlowSummary reportData)
        {
            try
            {
                if (!string.IsNullOrEmpty(reportCode))
                    reportCode = "RC " + reportCode;

                rdlcViewer.Reset();
                rdlcViewer.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                
                rdlcViewer.LocalReport.ReportEmbeddedResource = "PEA.BPM.CashManagementModule.Reports.MoneyFlowSummaryReport.rdlc";

                if (reportData.FlowList.Count > 0)
                {
                    ReportDataSource sReportDataSource = new ReportDataSource();
                    sReportDataSource.Name = "PEA_BPM_CashManagementModule_Interface_BusinessEntities_FlowSummaryInfo";
                    sReportDataSource.Value = reportData.FlowList;
                    rdlcViewer.LocalReport.DataSources.Add(sReportDataSource);
                }
                else
                {
                    //tell user and just quite
                    MessageBox.Show("ไม่พบรายการรับ-จ่ายของรายการนี้\nกรุณาเลือกรายการใหม่", "คำเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                    return;
                }

                //cash amount as parameter
                ReportParameter[] rParam = new ReportParameter[8];
                rParam[0] = new ReportParameter("parCashierId", reportData.CashierId);
                rParam[1] = new ReportParameter("parCashierName", reportData.CashierName);
                rParam[2] = new ReportParameter("parPosId", reportData.PosId);
                rParam[3] = new ReportParameter("parOpenWorkDate", reportData.OpenWorkDate);
                rParam[4] = new ReportParameter("parCloseWorkDate", reportData.CloseWorkDate);
                rParam[5] = new ReportParameter("parBranchName", Session.Branch.Name);
                rParam[6] = new ReportParameter("parDayCount", reportData.DayCount.Value.ToString());
                rParam[7] = new ReportParameter("parReportCode", reportCode);
                rdlcViewer.LocalReport.SetParameters(rParam);
                rdlcViewer.SetDisplayMode(DisplayMode.PrintLayout);
                rdlcViewer.ZoomMode = Microsoft.Reporting.WinForms.ZoomMode.PageWidth;
                //rdlcViewer.RefreshReport();               
            }
            catch (Exception e)
            {
                this.Cursor = Cursors.Default;
                ServiceHelper.TransformErrorMessage(e);
            }
        }

        public void Preview(ReportBankPayInDetailInfo reportData)
        {
            try
            {
                rdlcViewer.Reset();                
                rdlcViewer.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
                
                if (reportData.ChqList.Count > 0)
                {
                    ReportDataSource sReportDataSource = new ReportDataSource();
                    rdlcViewer.LocalReport.ReportEmbeddedResource = "PEA.BPM.CashManagementModule.Reports.PayInDetailReport.rdlc";
                    sReportDataSource.Name = "PEA_BPM_CashManagementModule_Interface_BusinessEntities_ChequeInfo";
                    sReportDataSource.Value = reportData.ChqList;
                    rdlcViewer.LocalReport.DataSources.Add(sReportDataSource);
                    //cash amount as parameter
                    ReportParameter[] rParam = new ReportParameter[7];
                    rParam[0] = new ReportParameter("parGLBankKey", reportData.GLBankKey);
                    rParam[1] = new ReportParameter("parGLBankName", reportData.GLBankName);
                    rParam[2] = new ReportParameter("parGLAccNo", reportData.GLBankAcc);
                    rParam[3] = new ReportParameter("parClearingAccNo", reportData.ClearingAccNo);
                    rParam[4] = new ReportParameter("parCashierName", string.Format("({0}) {1}", Session.User.Id, Session.User.Name ));
                    rParam[5] = new ReportParameter("parPaymentDate", reportData.PaymentDate);
                    rParam[6] = new ReportParameter("parBranchName", Session.Branch.Name);
                    rdlcViewer.LocalReport.SetParameters(rParam);
                    rdlcViewer.SetDisplayMode(DisplayMode.PrintLayout);
                    rdlcViewer.ZoomMode = Microsoft.Reporting.WinForms.ZoomMode.PageWidth;
                }

                if (reportData.CashAmt > 0 && reportData.ChqList.Count == 0)
                {
                    MessageBox.Show("รายการนำส่งที่เลือก ไม่มีเช็คในรายการ\nกรุณากดปุ่ม OK เพื่อทำงานต่อ", "ไม่พบข้อมูลเช็ค", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                    _presenter.OnCloseView();
                }
            }
            catch (Exception e)
            {
                this.Cursor = Cursors.Default;
                ServiceHelper.TransformErrorMessage(e);
            }
        }

