        public void Preview(BindingList<ChequeInfo> reportData, string closeWorkDate )
        {
            try
            {
                rdlcViewer.Reset();
                rdlcViewer.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;

                if (reportData.Count > 0)
                {
                    ReportDataSource sReportDataSource = new ReportDataSource();
                    rdlcViewer.LocalReport.ReportEmbeddedResource = "PEA.BPM.CashManagementModule.Reports.ChequeDailyRemain.rdlc";
                    sReportDataSource.Name = "PEA_BPM_CashManagementModule_Interface_BusinessEntities_ChequeInfo";
                    sReportDataSource.Value = reportData;
                    rdlcViewer.LocalReport.DataSources.Add(sReportDataSource);
                    //cash amount as parameter
                    ReportParameter[] rParam = new ReportParameter[3];
                    rParam[0] = new ReportParameter("parCashierName", string.Format("({0}) {1}", Session.User.Id, Session.User.Name));
                    rParam[1] = new ReportParameter("parPaymentDate", string.Format("{0}/{1}/{2}", closeWorkDate.Substring(0,2), closeWorkDate.Substring(2,2), closeWorkDate.Substring(4,4)));
                    rParam[2] = new ReportParameter("parBranchName", Session.Branch.Name);
                    rdlcViewer.LocalReport.SetParameters(rParam);
                    rdlcViewer.SetDisplayMode(DisplayMode.PrintLayout);
                    rdlcViewer.ZoomMode = Microsoft.Reporting.WinForms.ZoomMode.PageWidth;
