using System;
using System.Windows.Forms;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.CashManagementModule.Interface.Services;
using PEA.BPM.CashManagementModule.Interface.BusinessEntities;
using System.Collections.Generic;
using PEA.BPM.CashManagementModule.Interface.Constants;

namespace PEA.BPM.CashManagementModule
{
    public class TransferResponseViewPresenter : Presenter<ITransferResponseView>
    {
        private ICashManagementServices _services;
        private ICashReportServices _cashReportServices;

        [InjectionConstructor]
        public TransferResponseViewPresenter([ServiceDependency] ICashManagementServices services,
                                                                 ICashReportServices cashReportServices)
        {
            _services = services;
            _cashReportServices = cashReportServices;
        }

        public void LoadTransferedRequestItem(string cashierId)
        {
            View.TransferItem = _services.LoadTransferedRequestItem(cashierId);
        }

        public List<CashierMoneyTransferInfo> IsAnyTransferItemCanceled(string cashierId)
        {
            return _services.LoadTransferedRequestItem(cashierId);
        }

        public string ResponseTransferedItems(List<String> transferId, string workId, string status, string posId, string branchId, string modifiedBy)
        {
            return _services.ResponseTransferedItems(transferId, workId, status, posId, branchId, modifiedBy);
        }

        [EventPublication(EventTopicNames.RefreshTransferScreen, PublicationScope.Global)]
        public event EventHandler<EventArgs> RefreshTransferScreenHandler;
        public void RefreshMoneyInTray()
        {
            if (RefreshTransferScreenHandler != null)
                RefreshTransferScreenHandler(this, new EventArgs());
        }

        [EventPublication(EventTopicNames.ShowTransferResponse, PublicationScope.Global)]
        public event EventHandler<EventArgs> ShowTransferResponseHandler;
        public void ShowDialog()
        {
            if (ShowTransferResponseHandler != null)
                ShowTransferResponseHandler(this, new EventArgs());
        }

        [EventPublication(EventTopicNames.CashierOpenWork, PublicationScope.Global)]
        public event EventHandler<EventArgs<string>> CashierOpenWorkHandler;
        public void OnCashierOpenWork(string tmp)
        {
            if (CashierOpenWorkHandler != null)
                CashierOpenWorkHandler(this, new EventArgs<string>(tmp));
        }

        //[EventSubscription(EventTopicNames.WaitCursor, Thread = ThreadOption.UserInterface)]
        //public void ShowWaitCursor(object sender, EventArgs<bool> e)
        //{
        //    View.OnWaitCursor(e.Data);
        //}

        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }
    }
}

