using Microsoft.Practices.CompositeUI;
using System;
using Microsoft.Practices.CompositeUI.SmartParts;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.Infrastructure.Interface.Constants;

namespace PEA.BPM.Infrastructure.Interface
{
    public abstract class Presenter<TView> : IDisposable
    {
        private TView _view;
        private WorkItem _workItem;

        public TView View
        {
            get { return _view; }
            set { _view = value; OnViewSet(); }
        }

        [ServiceDependency]
        public WorkItem WorkItem
        {
            get { return _workItem; }
            set { _workItem = value; }
        }

        protected Form FindForm(string workspaceName)
        {
            return ((Control)WorkItem.Workspaces[workspaceName].SmartParts[0]).FindForm();
        }

        protected virtual void CloseView()
        {
            Services.IWorkspaceLocatorService locator = WorkItem.Services.Get<Services.IWorkspaceLocatorService>();
            IWorkspace wks = locator.FindContainingWorkspace(WorkItem, View);
            if (wks != null)
                wks.Close(View);
        }

        public virtual void OnViewReady() { }
        protected virtual void OnViewSet() { }

        ~Presenter()
        {
            Dispose(false);
        }

        /// <summary>
        /// See <see cref="System.IDisposable.Dispose"/> for more information.
        /// </summary>
        public void Dispose()
        {
            Dispose(true);
            GC.SuppressFinalize(this);
        }

        /// <summary>
        /// Called when the object is being disposed or finalized.
        /// </summary>
        /// <param name="disposing">True when the object is being disposed (and therefore can
        /// access managed members); false when the object is being finalized without first
        /// having been disposed (and therefore can only touch unmanaged members).</param>
        protected virtual void Dispose(bool disposing)
        {
            if (disposing)
            {
                if (_workItem != null)
                    _workItem.Items.Remove(this);
            }
        }

        [EventPublication(EventTopicNames.EnableMenuItem, PublicationScope.Global)]
        public event EventHandler<EventArgs<string>> EnableMenuItemHandler;
        public void EnableMenuItem(string command)
        {
            if (EnableMenuItemHandler != null)
                EnableMenuItemHandler(this, new EventArgs<string>(command));
        }

        [EventPublication(EventTopicNames.DisableMenuItem, PublicationScope.Global)]
        public event EventHandler<EventArgs<string>> DisableMenuItemHandler;
        public void DisableMenuItem(string command)
        {
            if (DisableMenuItemHandler != null)
                DisableMenuItemHandler(this, new EventArgs<string>(command));
        }
    }
}

