using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.PaymentCollectionModule.Interface.BusinessEntities.Reports;
using PEA.BPM.Architecture.ArchitectureTool;
using PEA.BPM.Infrastructure.Interface.BusinessEntities;
using System.Collections.Generic;
using PEA.BPM.Architecture.ArchitectureTool.Control;
using System.Drawing;
using PEA.BPM.Architecture.CommonUtilities;


namespace PEA.BPM.PaymentCollectionModule
{
    [SmartPart]
    public partial class CAC04CriteriaView : UserControl, ICAC04CriteriaView
    {
        public CAC04CriteriaView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public CAC04CriteriaViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
            bankKeySortingRadioButton.Checked = true;
            allReportRadioButton.Checked = true;
            LoadBranch();
            LoadBank();
        }

        private void LoadBranch()
        {
            List<Branch> branch = new List<Branch>(CodeTable.Instant.ListBranches());

            AutoCompleteTextBox<Branch> actb = new AutoCompleteTextBox<Branch>(branchIdMaskedTextBox);
            actb.PopupBackColor = Color.White;
            actb.PopupHeight = 100;
            actb.PopupExtendWidth = 80;
            actb.DataSource = branch;
            actb.KeyPress += new KeyPressEventHandler(collectorIdMaskedTextBox_KeyPress);
            AutoCompleteTextBox<Branch>.SetSelectedItem(branchIdMaskedTextBox, Session.Branch.Id);            
        }

        private void LoadBank()
        {
            List<Bank> bank = new List<Bank>(CodeTable.Instant.ListBanksByBusinessPlace(Session.Branch.Id.Substring(0, 4)));
            bank.Insert(0, new Bank("", "เลือกทั้งหมด"));
            bankldComboBox.ValueMember = "BankKey";
            bankldComboBox.DisplayMember = "BankName";
            bankldComboBox.DataSource = bank;
        }

        private void displayReportButton_Click(object sender, EventArgs e)
        {
            displayReport();
        }

        private void displayReport()
        {
            DateTime fromDate = transactionFromDateTimePicker.Value.Date;
            DateTime toDate = transactionToDateTimePicker.Value.Date;
            //DateTime? toDate = null;

            TimeSpan DaySpan = toDate.Subtract(fromDate);
            int DayDiff = DaySpan.Days + 1;

            if (AutoCompleteTextBox<Branch>.GetSelectedItem(branchIdMaskedTextBox) == null)
            {
                MessageBox.Show("กรุณาใส่รหัส กฟฟ.", "ข้อความเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return;
            }
            else if (fromDate > toDate)
            {
                MessageBox.Show("กรุณาเลือกวันที่รับชำระให้น้อยกว่าหรือเท่ากับวันที่ " + String.Format("{0:dd/MM/yyyy}", toDate), "ข้อความเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return;
            }
            else if (DayDiff > 31)
            {
                MessageBox.Show("กรุณาเลือกช่วงวันที่รับชำระไม่เกิน 31 วัน", "ข้อความเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return;
            }

            try
            {
                if (fromTimeMaskedTextBox.Text.Replace(":", "").Trim() != "")
                {
                    DateTime.Parse(DateTime.Now.ToShortDateString() + " " + fromTimeMaskedTextBox.Text.Trim());
                }
                if (toTimeMaskedTextBox.Text.Replace(":", "").Trim() != "")
                {
                    DateTime.Parse(DateTime.Now.ToShortDateString() + " " + toTimeMaskedTextBox.Text.Trim());
                }
            }
            catch
            {
                MessageBox.Show("กรุณาระบุเวลาให้ถูกต้อง", "ข้อความเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return;
            }
            
            if (fromTimeMaskedTextBox.Text.Replace(":", "").Trim() != ""
                    && toTimeMaskedTextBox.Text.Replace(":", "").Trim() != ""
                    && DateTime.Parse(DateTime.Now.ToShortDateString() + " " + fromTimeMaskedTextBox.Text.Trim()) > DateTime.Parse(DateTime.Now.ToShortDateString() + " " + toTimeMaskedTextBox.Text.Trim()))
            {
                MessageBox.Show("กรุณาเลือกช่วงเวลาให้ถูกต้อง", "ข้อความเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return;
            }

            CAC01Param param = new CAC01Param();
            param.BranchId = (AutoCompleteTextBox<Branch>.GetSelectedItem(branchIdMaskedTextBox) == null) ? null : AutoCompleteTextBox<Branch>.GetSelectedItem(branchIdMaskedTextBox).BranchId;
            param.ControllerId = (collectorIdMaskedTextBox.Text.Trim() == string.Empty) ? null : collectorIdMaskedTextBox.Text.Trim().PadLeft(8, '0');
            param.TransFromDate = DateTime.SpecifyKind(transactionFromDateTimePicker.Value, DateTimeKind.Unspecified);
            param.TransToDate = DateTime.SpecifyKind(transactionToDateTimePicker.Value, DateTimeKind.Unspecified);
            //param.TransToDate = null;
            param.FromTime = (fromTimeMaskedTextBox.Text.Replace(":", "").Trim() == "") ? null : fromTimeMaskedTextBox.Text + ":00";
            param.ToTime = (toTimeMaskedTextBox.Text.Replace(":", "").Trim() == "") ? null : toTimeMaskedTextBox.Text + ":59";
            param.BankKey = (bankldComboBox.SelectedIndex == 0 || bankldComboBox.SelectedIndex == -1) ? null : ((Bank)bankldComboBox.SelectedItem).BankKey;
            param.BankName = (bankldComboBox.SelectedIndex == 0 || bankldComboBox.SelectedIndex == -1) ? bankldComboBox.Text.ToString() : ((Bank)bankldComboBox.SelectedItem).BankName;

            if (allReportRadioButton.Checked == true && bankKeySortingRadioButton.Checked == true)
            {
                param.Report = ReportName.CAC04_1;
            }
            else if (allReportRadioButton.Checked == true && tranfTimeSortingRadioButton.Checked == true)
            {
                param.Report = ReportName.CAC04_2;
            }
            else if (detailReportRadioButton.Checked == true && bankKeySortingRadioButton.Checked == true)
            {
                param.Report = ReportName.CAC04_3;
            }
            else if (detailReportRadioButton.Checked == true && tranfTimeSortingRadioButton.Checked == true)
            {
                param.Report = ReportName.CAC04_4;
            }        

            displayReportButton.Enabled = false;
            _presenter.OnShowReport(param);
            displayReportButton.Enabled = true;
        }

        private void collectorIdMaskedTextBox_KeyPress(object sender, KeyPressEventArgs e)
        {
            if (e.KeyChar == (char)13)
            {
                displayReport();
            }
        }

        private void fromTimeMaskedTextBox_TextChanged(object sender, EventArgs e)
        {
            if (fromTimeMaskedTextBox.Text.Length == 5)
            {
                toTimeMaskedTextBox.Focus();
            }
        }

        private void transactionFromDateTimePicker_ValueChanged(object sender, EventArgs e)
        {
            DateTime fromDate = transactionFromDateTimePicker.Value.Date;
            DateTime toDate = transactionToDateTimePicker.Value.Date;

            if (fromDate != toDate)
            {
                fromTimeMaskedTextBox.Enabled = false;
                toTimeMaskedTextBox.Enabled = false;
            }
            else
            {
                fromTimeMaskedTextBox.Enabled = true;
                toTimeMaskedTextBox.Enabled = true;
            }
        }

        private void transactionToDateTimePicker_ValueChanged(object sender, EventArgs e)
        {
            DateTime fromDate = transactionFromDateTimePicker.Value.Date;
            DateTime toDate = transactionToDateTimePicker.Value.Date;

            if (fromDate != toDate)
            {
                fromTimeMaskedTextBox.Enabled = false;
                toTimeMaskedTextBox.Enabled = false;
            }
            else
            {
                fromTimeMaskedTextBox.Enabled = true;
                toTimeMaskedTextBox.Enabled = true;
            }
        }

    }
}

