        private void InitializeRenderExtensions(string reportName)
        {
            EnableRenderExtension("Excel", "Excel", reportName);
            EnableRenderExtension("PDF", "Acrobat (PDF) file", reportName);
        }

        /// <summary>
        /// Enable render extension for ReportViewer.LocalReport.
        /// </summary>
        /// <param name="extensionName">
        /// Extension name. Is taken from Microsoft.Reporting.ControlService.ListRenderingExtensions
        /// - HTML4.0
        /// - Excel
        /// - RGDI
        /// - IMAGE
        /// - PDF
        /// </param>
        /// <remarks>
        /// http://www.codeproject.com/KB/reporting-services/report-viewer-reflection.aspx
        /// http://beaucrawford.net/post/Enable-HTML-in-ReportViewer-LocalReport.aspx
        /// </remarks>
        private void EnableRenderExtension(string extensionName, string localizedExtensionName, string reportName)
        {
            foreach (var extension in RenderingExtensions)
            {
                // name = extension.Name;
                var name = extension
                    .GetType()
                    .GetProperty("Name")
                    .GetValue(extension, null)
                    .ToString();

                if (name == extensionName)
                {
                    if (name == "Excel" && reportName == "PEA.BPM.PaymentCollectionModule.Reports.CAC05_1Report.rdlc")
                    {
                        // extension.m_isVisible = true;
                        extension
                            .GetType()
                            .GetField("m_isVisible", BindingFlags.NonPublic | BindingFlags.Instance)
                            .SetValue(extension, false);
                    }
                    else if (name == "Excel" && reportName != "PEA.BPM.PaymentCollectionModule.Reports.CAC05_1Report.rdlc")
                    {
                        // extension.m_isVisible = true;
                        extension
                            .GetType()
                            .GetField("m_isVisible", BindingFlags.NonPublic | BindingFlags.Instance)
                            .SetValue(extension, true);
                    }

                    // extension.m_isExposedExternally = true;
                    extension
                        .GetType()
                        .GetField("m_isExposedExternally", BindingFlags.NonPublic | BindingFlags.Instance)
                        .SetValue(extension, true);

                    // extension.m_localizedName = localizedExtensionName;
                    extension
                        .GetType()
                        .GetField("m_localizedName", BindingFlags.NonPublic | BindingFlags.Instance)
                        .SetValue(extension, localizedExtensionName);
                }
            }
        }


        #region IResultView Members

        /// <summary>
        /// Extension list that is used for report rendring
        /// </summary>
        /// <remarks>
        /// viewer.LocalReport.m_previewService.ListRenderingExtensions( )
        /// </remarks>
        private IList RenderingExtensions
        {
            get
            {
                var service = rdlcViewer.LocalReport
                    .GetType()
                    .GetField("m_previewService", BindingFlags.NonPublic | BindingFlags.Instance)
                    .GetValue(rdlcViewer.LocalReport);

                var extensions = service
                    .GetType()
                    .GetMethod("ListRenderingExtensions")
                    .Invoke(service, null);

                return (IList)extensions;
            }
        }


        public void ShowReport(string reportName, ReportDataSource dataSource, ReportParameter[] rParam)
        {
            rdlcViewer.Reset();
            rdlcViewer.ProcessingMode = Microsoft.Reporting.WinForms.ProcessingMode.Local;
            rdlcViewer.LocalReport.ReportEmbeddedResource = reportName;
            rdlcViewer.LocalReport.DataSources.Add(dataSource);
            if (rParam != null)
            {
                rdlcViewer.LocalReport.SetParameters(rParam);
            }
            rdlcViewer.RefreshReport();
            InitializeRenderExtensions(reportName);
        }

        #endregion

