using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Architecture.ArchitectureTool.ErrorHandling;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.Infrastructure.Library.UI;
using System.Windows.Forms;
using PEA.BPM.PaymentCollectionModule.Interface.Constants;
using PEA.BPM.PaymentCollectionModule.Interface.Services;
using PEA.BPM.PaymentCollectionModule.Interface.BusinessEntities;
using System.Collections.Generic;
using PEA.BPM.Architecture.CommonUtilities;

namespace PEA.BPM.PaymentCollectionModule
{
    public class PaymentHistoryViewPresenter : Presenter<IPaymentHistoryView>
    {
        private IBillingService _billingService;
        
		[InjectionConstructor]
        public PaymentHistoryViewPresenter([ServiceDependency] IBillingService billingService)
		{
            _billingService = billingService;
		}

        [EventSubscription(Constants.EventTopicNames.ViewHistoryClick, Thread = ThreadOption.UserInterface)]
        public void ViewHistoryClickHandler(object sender, EventArgs<HistoryViewParam> e)
        {
            try
            {
                Customer customer = _billingService.GetCustomerDetailOnPaymentHistory(e.Data);
                List<PaidInvoice> paidInvoices = _billingService.GetPaymentHistory(e.Data);
                
                if (customer!=null)
                {
                    View.CustomerDetail = customer;
                    View.PaidInvoices = paidInvoices;
                    ShowView();
                }
                else
                {
                    MessageBox.Show("ไม่พบข้อมูลดังกล่าวอยู่ในระบบ", "ข้อความ", MessageBoxButtons.OK, MessageBoxIcon.Information);
                }
            }
            catch (Exception ex)
            {
                Logger.WriteError(Logger.Module.POS, "ดูประวัติการชำระเงิน", ex.ToString());
                ClientExceptionController.ShowExceptionDialog(EErrorInModule.POS, ex);
            }
        }

        [EventSubscription(EventTopicNames.OnCloseAllViews, Thread = ThreadOption.UserInterface)]
        public void OnCloseAllViewsHandler(object sender, EventArgs e)
        {
            OnCloseView();
        }

        private void ShowView()
        {
            WindowSmartPartInfo info = new WindowSmartPartInfo();
            info.Modal = true;
            info.Keys.Add(WindowWorkspaceSetting.StartPosition, FormStartPosition.CenterParent);
            info.Keys.Add(WindowWorkspaceSetting.FormBorderStyle, FormBorderStyle.FixedToolWindow);
            info.Keys.Add(WindowWorkspaceSetting.AcceptButton, View.CloseButton);
            info.Keys.Add(WindowWorkspaceSetting.CancelButton, View.CloseButton); 
            info.MaximizeBox = false;
            info.MinimizeBox = false;
            info.Title = " ประวัติการชำระเงิน";
            WorkItem.Workspaces[WorkspaceNames.ModalWindows].Show(View, info);
        }

        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }
    }
}

