using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using System.Collections.Generic;
using System.Windows.Forms;
using PEA.BPM.PaymentCollectionModule.Interface.Services;
using PEA.BPM.Architecture.ArchitectureInterface.Constants;
using PEA.BPM.PaymentCollectionModule.Interface.BusinessEntities;
using PEA.BPM.PaymentCollectionModule.Interface.Constants;
using PEA.BPM.Architecture.CommonUtilities;

namespace PEA.BPM.PaymentCollectionModule
{
    public class ReceiptGenerateResultViewPresenter : Presenter<IReceiptGenerateResultView>
    {
        private IBillingService _billingService;

        #region Init

        [InjectionConstructor]
        public ReceiptGenerateResultViewPresenter([ServiceDependency] IBillingService billingService)
		{
            this._billingService = billingService;
        }

        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }

        #endregion

        #region Event Subscription

        [EventSubscription(EventTopicNames.SearchPaymentNonReceipt, Thread = ThreadOption.UserInterface)]
        public void SearchPaymentNonReceiptHandler(object sender, EventArgs<PaymentNonReceiptInfo> e)
        {
            List<PaymentNonReceiptInfo> paymentList = _billingService.SearchPaymentNonReceipt(e.Data);
            if (paymentList.Count > 0)
            {
                View.SetPaymentSearchResult(paymentList);
            }
            else
            {
                WaitingFormHelper.HideWaitingForm();
                MessageBox.Show("ไม่พบข้อมูลการรับเงินตามเงื่อนไขที่กำหนด", "ข้อความ", MessageBoxButtons.OK, MessageBoxIcon.Information);
                View.ResetPaymentSearchResult();
            }
        }

        [EventSubscription(EventTopicNames.OnlineStatus, Thread = ThreadOption.UserInterface)]
        public void OnlineStatusHandler(object sender, EventArgs<bool> e)
        {
            View.EnablePOSPanel(e.Data);
        }

        
        #endregion


        #region Internal Function

        internal void CreateReceiptService(List<PaymentNonReceiptInfo> paymentGenReList)
        {
            _billingService.CreateReceiptService(paymentGenReList);
        }

        #endregion

    }
}

