namespace PEA.BPM.PaymentCollectionModule
{
    public class ReceiptSearchResultViewPresenter : Presenter<IReceiptSearchResultView>
    {
        private IPaidBillService _paidBillService;

        [InjectionConstructor]
        public ReceiptSearchResultViewPresenter([ServiceDependency] IPaidBillService paidBillService)
        {
            _paidBillService = paidBillService;
        }


        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }

        private List<Receipt> RemoveReceiptIdThatIsMemberOfGroupReceipt(List<Receipt> receipts)
        {
            #region DCR รวมใบเสร็จแผนผ่อน 2021AUG พิมพ์ซ่อมใบเสร็จรับเงิน (ReceiptSearchResultView)


            List<Receipt> result = receipts;

            bool init       = false;
            var ReceiptText = new StringBuilder();
            foreach (var r in receipts)
            {
                if (!String.IsNullOrEmpty(r.ReceiptId))
                {
                    if (init == false)
                    {
                        ReceiptText.Append(r.ReceiptId);
                        init = true;                        
                    }
                    else
                    {
                        ReceiptText.Append("|");
                        ReceiptText.Append(r.ReceiptId);
                    }
                }
            }

            return result;

            #endregion


        }

        [EventSubscription(EventTopicNames.ReceiptSearch, Thread = ThreadOption.UserInterface)]
        public void ReceiptSearchHandler(object sender, EventArgs<ReceiptSearchParam> e)
        {
            try
            {
                WaitingFormHelper.ShowWaitingForm();

                List<Receipt> receipts = _paidBillService.SearchReceipt(e.Data);



                if (receipts.Count == 0)
                {
                    WaitingFormHelper.HideWaitingForm();
                    MessageBox.Show("ไม่พบข้อมูลใบเสร็จที่ค้นหา\n\n*หมายเหตุ - สำหรับการค้นหาโดย 'เลขที่ใบเสร็จ' หรือ 'เลขประจำตัวผู้ชำระหนี้' ต้องกรอกข้อมูลให้ครบทุกหลัก",
                        "ค้นหาข้อมูล", MessageBoxButtons.OK, MessageBoxIcon.Exclamation);
                }
                else if (receipts.Count == 1)
                {
                    View.Receipts = receipts;
                    WaitingFormHelper.HideWaitingForm();
                    ShowView();
                    //OnReceiptItemAdd(receipts);
                }
                else
                {
                    View.Receipts = receipts;
                    WaitingFormHelper.HideWaitingForm();
                    ShowView();
                }
            }
            catch (Exception ex)
            {
                WaitingFormHelper.HideWaitingForm();
                Logger.WriteError(Logger.Module.POS, "ค้นหาข้อมูลใบเสร็จรับเงิน", ex.ToString());
                MessageBox.Show("ไม่สามารถติดต่อกับเครื่องแม่ข่ายได้\nโปรดตรวจสอบการเชื่อมโยงของระบบเครือข่าย หรือติดต่อผู้ดูแลระบบ",
                    "ข้อผิดพลาด", MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
        }

        [EventPublication(EventTopicNames.ReceiptItemAdd, PublicationScope.WorkItem)]
        public event EventHandler<EventArgs<List<Receipt>>> ReceiptItemAdd;
        internal void OnReceiptItemAdd(List<Receipt> receipts)
        {
            if (ReceiptItemAdd != null)
                ReceiptItemAdd(this, new EventArgs<List<Receipt>>(receipts));
        }

        private void ShowView()
        {
            WindowSmartPartInfo info = new WindowSmartPartInfo();
            info.Modal = true;
            info.Keys.Add(WindowWorkspaceSetting.StartPosition, FormStartPosition.CenterParent);
            info.Keys.Add(WindowWorkspaceSetting.FormBorderStyle, FormBorderStyle.FixedToolWindow);
            info.Keys.Add(WindowWorkspaceSetting.AcceptButton, View.OkButton);
            info.Keys.Add(WindowWorkspaceSetting.CancelButton, View.CancelButton);
            info.MaximizeBox = false;
            info.MinimizeBox = false;
            info.Title = " ผลการค้นหาใบเสร็จ";
            WorkItem.Workspaces[WorkspaceNames.ModalWindows].Show(View, info);
        }

        [EventSubscription(EventTopicNames.ActionSuccess, Thread = ThreadOption.UserInterface)]
        public void ActionSuccessHandler(object sender, EventArgs e)
        {
            if (WorkItem.Workspaces[WorkspaceNames.ModalWindows].ActiveSmartPart == View)
            {
                ((UserControl)View).ParentForm.Close();
            }
        }

        [EventSubscription(EventTopicNames.OnCloseAllViews, Thread = ThreadOption.UserInterface)]
        public void OnCloseAllViewsHandler(object sender, EventArgs e)
        {
            OnCloseView();
        }
    }
}

