using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.PaymentCollectionModule.Interface.BusinessEntities;
using PEA.BPM.PaymentCollectionModule.Interface.Constants;
using PEA.BPM.Infrastructure.Library.UI;
using System.Windows.Forms;

namespace PEA.BPM.PaymentCollectionModule
{
    public class GroupInvoicingReportPresenter : Presenter<IGroupInvoicingReport>
    {
        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }

        [EventSubscription(EventTopicNames.ShowGroupInvoicingReport, Thread = ThreadOption.UserInterface)]
        public void ShowGroupInvoicingReportHandler(object sender, EventArgs<GroupInvoicingReportParam> e)
        {
            GroupInvoicingReportParam param = e.Data;
            View.ShowReport(param.GroupInvoiceNo, param.ReceiptId, param.ReportService);
            ShowView();
        }

        public void ShowView()
        {
            WindowSmartPartInfo info = new WindowSmartPartInfo();
            info.Modal = true;
            info.Keys.Add(WindowWorkspaceSetting.StartPosition, FormStartPosition.CenterParent);
            info.Keys.Add(WindowWorkspaceSetting.FormBorderStyle, FormBorderStyle.FixedToolWindow);
            info.MaximizeBox = false;
            info.MinimizeBox = false;
            info.Title = " ผลการค้นหาข้อมูลหนี้ที่มีในระบบ";
            WorkItem.Workspaces[WorkspaceNames.ModalWindows].Show(View, info);
        }
    }
}

