using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.PaymentManagementModule.Constants;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.PaymentManagementModule.Interface.BusinessEntities;
using System.Collections.Generic;
using PEA.BPM.Architecture.CommonUtilities;
using System.Windows.Forms;
using PEA.BPM.PaymentManagementModule.Interface.Services;
using PEA.BPM.CashManagementModule.Interface.Services;
using PEA.BPM.CashManagementModule.Interface.BusinessEntities;

namespace PEA.BPM.PaymentManagementModule
{
    public class ToBeCancelledPaymentVoucherViewPresenter : Presenter<IToBeCancelledPaymentVoucherView>
    {
        private IPaymentMntService _PaymentMntService;

        public ToBeCancelledPaymentVoucherViewPresenter([ServiceDependency] IPaymentMntService PaymentMntService)
        {
            _PaymentMntService = PaymentMntService;
        }
        
        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }

        [EventSubscription(EventTopicNames.PaymentVoucherItemAdd, Thread = ThreadOption.UserInterface)]
        public void PaymentVoucherItemAddHandler(object sender, EventArgs<List<APEntity>> e)
        {
            try
            {
                if (View.AddEntities(e.Data))
                {
                    OnActionSuccess();
                }
            }
            catch (Exception ex)
            {
                Logger.WriteError(Logger.Module.POS, "เพิ่มรายการใบสำคัญจ่ายเพื่อยกเลิก", ex.ToString());
                MessageBox.Show("พบข้อผิดพลาดในการเพิ่มใบสำคัญจ่ายเพื่อการยกเลิก\nโปรดติดต่อผู้ดูแลระบบ", "ข้อผิดพลาด",
                    MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
        }

        [EventPublication(EventTopicNames.ActionSuccess, PublicationScope.WorkItem)]
        public event EventHandler<EventArgs> ActionSuccess;
        internal void OnActionSuccess()
        {
            try
            {
                if (ActionSuccess != null)
                    ActionSuccess(this, new EventArgs());
            }
            catch (Exception) { }
        }

        [EventSubscription(EventTopicNames.OnCloseAllViews, Thread = ThreadOption.UserInterface)]
        public void OnCloseAllViewsHandler(object sender, EventArgs e)
        {
            OnCloseView();
        }

        internal List<APEntity> OnCancelPaymentVoucher(string strLinePaymentVoucher, string reason, string cashierId, string cashierName)
        {
            List<APEntity> repayments = _PaymentMntService.UpdateAPByStrLineAPId(strLinePaymentVoucher, reason, cashierId, cashierName);
            return repayments;
        }


        #region +++ Cash Management +++

        [EventPublication(EventTopicNames.CashierOpenWork, PublicationScope.Global)]
        public event EventHandler<EventArgs<string>> CashierOpenWorkHandler;
        public void OnCashierOpenWork(string tmp)
        {
            if (CashierOpenWorkHandler != null)
                CashierOpenWorkHandler(this, new EventArgs<string>(tmp));
        }

        [EventPublication(EventTopicNames.ClosePaymentView, PublicationScope.Global)]
        public event EventHandler<EventArgs> ClosePaymentView;
        public void OnClosePaymentView()
        {
            if (ClosePaymentView != null)
                ClosePaymentView(this, new EventArgs());
        }

        #endregion
    }
}

