using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;

namespace PEA.BPM.ToolModule
{
    [SmartPart]
    public partial class EnviSetupView : UserControl, IEnviSetupView
    {
        public EnviSetupView()
        {
            InitializeComponent();
        }


        public IRegisterView RegisterTab
        {
            set 
            {             
                registerWorkSpace.Show(value);
            }
        }

        public IServerConfigView ServerConfigTab
        {
            set
            {              
                serverWorkSpace.Show(value);
            }
        }


        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public EnviSetupViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {           
            _presenter.OnViewReady();
           // _presenter.LoadSetupData();
        }

        private void registerTab_SelectedIndexChanged(object sender, EventArgs e)
        {
            if (registerTab.SelectedIndex == 0) //server tab
                _presenter.ActivateTab(true);
            else if (registerTab.SelectedIndex == 1) //register tab
                _presenter.ActivateTab(false);
        }

        public void CloseView()
        {
            _presenter.OnCloseView();
        }
       
    }
}

