using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.ToolModule.Interface.BusinessEntities;

namespace PEA.BPM.ToolModule
{
    [SmartPart]
    public partial class RemarkEditView : UserControl, IRemarkEditView
    {
        UnlockRemark _unlockRemarkInfo;        

        public UnlockRemark UnlockRemarkInfo
        {
            set
            {
                _unlockRemarkInfo = value;
                oldRemarkText.Text = value.Description;
            }
        }

        public RemarkEditView()
        {
            InitializeComponent();
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public RemarkEditViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
        }

        private void okButton_Click(object sender, EventArgs e)
        {
            if (newRemarkText.Text.Trim() != string.Empty)
            {
                UnlockRemark remark = new UnlockRemark();
                remark.FncId = _unlockRemarkInfo.FncId;
                remark.RemarkId = _unlockRemarkInfo.RemarkId;
                remark.Description = newRemarkText.Text;

                _presenter.OnRemarkEdited(remark);
                _presenter.OnCloseView();
            }
            else
                MessageBox.Show("กรุณากรอก Remark", "ข้อผิดพลาด", MessageBoxButtons.OK, MessageBoxIcon.Error);
            
        }

        private void cancelButton_Click(object sender, EventArgs e)
        {
            _presenter.OnCloseView();
        }
    }
}

