using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using System.Collections.Generic;
using System.Windows.Forms;
using PEA.BPM.ePaymentsModule.Interface.Services;
using PEA.BPM.ePaymentsModule.Interface.Constants;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities.ReceiptPrinting;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities;
using PEA.BPM.ePaymentsModule.Services;


namespace PEA.BPM.ePaymentsModule
{
    public class PPrintedDepositResultViewPresenter : Presenter<IPPrintedDepositResultView>
    {
        private IReceiptPrintingService _receiptPrintingService;

        #region Init

        [InjectionConstructor]
        public PPrintedDepositResultViewPresenter([ServiceDependency] IReceiptPrintingService receiptPrintingService)
		{
            this._receiptPrintingService = receiptPrintingService;
        }

        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }

        #endregion

        #region Event Subscription

        [EventSubscription(EventTopicNames.SearchClearified, Thread = ThreadOption.UserInterface)]
        public void SearchClearifiedHandler(object sender, EventArgs<PPrintedDeposit> e)
        {
            List<PPrintedDeposit> clearifiedList = _receiptPrintingService.SearchDebtClearify(e.Data);
            if (clearifiedList.Count > 0)
            {
                View.SetClerifiedSearchResult(clearifiedList);
            }
            else
            {
                MessageBox.Show("ไม่พบข้อมูลการตรวจสอบหนี้ที่ต้องพิมพ์ใบรับฝากเงิน", "ข้อความ", MessageBoxButtons.OK, MessageBoxIcon.Information);
                View.ResetClerifiedList();
            }
        }

        [EventSubscription(EventTopicNames.ResetClearifiedList , Thread = ThreadOption.UserInterface)]
        public void ResetClearifiedListHandler(object sender, EventArgs e)
        {
            View.ResetClerifiedList();
        }

        
        #endregion

        #region Event Publication

       
  
        #endregion

        #region Internal Function


        internal List<PPrintedDeposit> GetCADepositPPrinted(List<PPrintedDeposit> paramList)
        {
            try
            {
                return _receiptPrintingService.GetCADepositPPrinted(paramList);
            }
            catch (Exception Ex)
            {
                throw;
            }
        }

        internal List<PPrintedDeposit> GetAgentDepositPPrinted(List<PPrintedDeposit> paramList)
        {
            try
            {
                return _receiptPrintingService.GetAgentDepositPPrinted(paramList);
            }
            catch (Exception Ex)
            {
                throw;
            }
        }

        #endregion

    }
}

