                param.UploadDt  = cmbUploadDt.Value;
                param.FixedDt = cmbClearify.Value;
                param.CompanyId = cmbAgent.SelectedValue == null ? null : cmbAgent.SelectedValue.ToString().Trim();

                if (cmbDepositType.SelectedIndex == 0)
