        #region Custom Function (Validation)

        private bool ValidateClearifiedSearchResult(bool dtSearch)
        {
            bool result = true;
            if (dtSearch)
            {
                if (cmbAgent.Items.Count == 0)
                {
                    result = false;
                    MessageBox.Show("ระบบไม่สามารถแสดงรายการพิมพ์ใบรับฝากเงินได้ เนื่องจากไม่พบข้อมูลของตัวแทน", "คำเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                }
            }
            else
            {
                if (txtReceiptId.Text.Trim() == "")
                {
                    result = false;
                    MessageBox.Show("กรุณาป้อนเลขที่ใบนำฝากเงินที่ต้องการ ก่อนกดปุ่มแสดงข้อมูล", "คำเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                }
            }
            return result;
        }

