        #region Custom Fucntion


        private void ResetSearchByDtControl()
        {
            cmbUploadDt.Value = DateTime.Now;
            cmbClearify.Value = DateTime.Now;
            if (cmbAgent.Items.Count > 0)
            {
                cmbAgent.SelectedIndex = 0;
            }
            if (cmbDepositType.Items.Count > 0)
            {
                cmbDepositType.SelectedIndex = 0;
            }
        }

        private void ResetSearchByReceiptIdControl()
        {
            txtReceiptId.ResetText();
        }

        private void LoadAgentAll()
        {
            List<Company> compList = _presenter.GetAgentAll();
            if (compList.Count > 0)
            {
                compList.Insert(0, new Company(null, "---เลือกทั้งหมด---"));
            }
            cmbAgent.DisplayMember = "CompanyName";
            cmbAgent.ValueMember = "CompanyId";
            cmbAgent.DataSource = compList.ToArray();
            cmbAgent.Refresh();

            if (cmbAgent.Items.Count > 0)
            {
                cmbAgent.SelectedIndex = 0;
            }
        }

        private void LoadDepositType()
        {
            cmbDepositType.Items.Clear();
            cmbDepositType.Items.Add("ใบรับฝากสำหรับผู้ใช้ไฟ");
            cmbDepositType.Items.Add("ใบรับฝากสำหรับตัวแทน");
            cmbDepositType.SelectedIndex = 0;
        }

