using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using System.Collections.Generic;
using System.Windows.Forms;
using PEA.BPM.ePaymentsModule.Interface.Services;
using PEA.BPM.ePaymentsModule.Interface.Constants;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities.ReceiptPrinting;
using PEA.BPM.ePaymentsModule.Services;

namespace PEA.BPM.ePaymentsModule 
{
    public class PrintPPrintedReceiptViewPresenter : Presenter<IPrintPPrintedReceiptView>
    {

        private IReceiptPrintingService _receiptPrintingService;
       
        [InjectionConstructor]
        public PrintPPrintedReceiptViewPresenter([ServiceDependency] IReceiptPrintingService receiptPrintingService)
		{
            _receiptPrintingService = receiptPrintingService;
		}

        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        public void OnCloseView()
        {
            base.CloseView();
        }

        #region Internal Function

        internal List<PPrintedReceipt> GetPPrintedDataSource(PPrintedReceiptParam param)
        {
            try
            {
                return _receiptPrintingService.GetPPrintedService(param);
            }
            catch (Exception Ex)
            {
                throw;
            }
        }

        internal List<Company> GetAgentAll()
        {
            try
            {
                return _receiptPrintingService.GetAgentAllService();
            }
            catch (Exception Ex)
            {
                throw;
            }
        }

        internal List<EPayUpload> SearchAgentPaymentNum(EPayUpload param)
        {
            try
            {
                return _receiptPrintingService.SearchAgentPaymentNumber(param);
            }
            catch (Exception Ex)
            {
                throw;
            }
        }

        #endregion

    }
}

