using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using PEA.BPM.Infrastructure.Interface;
using PEA.BPM.Architecture.ArchitectureTool;
using PEA.BPM.Infrastructure.Interface.BusinessEntities;
using System.Collections.Generic;
using PEA.BPM.Architecture.ArchitectureTool.Control;
using System.Drawing;
using PEA.BPM.Architecture.CommonUtilities;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities.Reports;
using PEA.BPM.Architecture.CommonUtilities;
using PEA.BPM.ePaymentsModule.Utilities;


namespace PEA.BPM.ePaymentsModule
{
    [SmartPart]
    public partial class RE_05CriteriaView : UserControl, IRE_05CriteriaView
    {

        public RE_05CriteriaView()
        {
            InitializeComponent();
        }

        public List<AccountClassInfo> AccountClassList
        {
            set
            {
                List<AccountClassInfo> acList = value;
                if (acList.Count > 0)
                {
                    ddlAccountClass.DisplayMember = "DisplayName";
                    ddlAccountClass.DataSource = acList;
                }
            }
        }

        public List<Company> CompanyList
        {
            set
            {
                List<Company> acList = value;
                if (acList.Count > 0)
                {
                    ddlAgent.DisplayMember = "DisplayName";
                    ddlAgent.DataSource = acList;
                }
            }
        }

        /// <summary>
        /// Sets the presenter. The dependency injection system will automatically
        /// create a new presenter for you.
        /// </summary>
        [CreateNew]
        public RE_05CriteriaViewPresenter Presenter
        {
            set
            {
                _presenter = value;
                _presenter.View = this;
            }
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
            loadAccountClassList();
            txtPeriod.Text = CommonUtilities.GetPeriod(DateTime.Now);
        }

        private void btnReport_Click(object sender, EventArgs e)
        {
            try
            {
                if (IsValid())
                {                    
                    RE05ParamInfo param = new RE05ParamInfo();
                    param.PayDate = DateFormatter.PeriodToDateTime(txtPeriod.Text.Trim());  
                    param.AccountClassId = ((AccountClassInfo)ddlAccountClass.SelectedItem).AccountClassId;
                    param.CompanyId = ((Company)ddlAgent.SelectedItem).CompanyId;
                    param.RunningBranchId = Session.Branch.Id;
                    _presenter.OnRE_05Report(param);
                }
            }
            catch (Exception ex)
            {
                WaitingFormHelper.HideWaitingForm();
                Logger.WriteError(Logger.Module.EPAYMENT, "แสดงรายงาน RE_05", ex.ToString());
                MessageBox.Show("ไม่สามารถติดต่อกับฐานข้อมูลได้ โปรดติดต่อผู้ดูแลระบบ\n", "ข้อผิดพลาด",
                   MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
        }

        private void btnClear_Click(object sender, EventArgs e)
        {
            clearData();
        }

        #region "Helper Function"
        private void clearData()
        {            
            txtPeriod.Clear();
            ddlAccountClass.SelectedIndex = 0;
            ddlAgent.SelectedIndex = 0;
            txtPeriod.Text = CommonUtilities.GetPeriod(DateTime.Now);
        }

        private void loadAccountClassList()
        {
            _presenter.LoadAccountClassList(PEA.BPM.ePaymentsModule.Interface.BusinessEntities.Reports.ReportName.RE_05);
        }

        private bool IsValid()
        {
            if (txtPeriod.Text.Trim().Length == 1)
            {
                MessageBox.Show("กรุณาระบุเดือนที่รับชำระสำหรับออกรายงาน", "คำเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return false;
            }
            else if (txtPeriod.Text.Trim().Length != 7)
            {
                MessageBox.Show("เดือนที่รับชำระมีรูปแบบไม่ถูกต้อง", "คำเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return false;
            }
            else if (txtPeriod.Text.Substring(3, 4).Trim().CompareTo("2500") < 0)
            {
                MessageBox.Show("ปีของเดือนที่รับชำระ ต้องมากกว่า 2500", "คำเตือน", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return false;
            }
            else if (DateFormatter.PeriodToDateTime(txtPeriod.Text.Trim()) == null)
            {
                MessageBox.Show("เดือน/ปีที่ระบุไม่ถูกต้อง", "ข้อผิดพลาด", MessageBoxButtons.OK, MessageBoxIcon.Error);
                txtPeriod.Clear();
                return false;
            }
            return true;
        }
        #endregion

        private void ddlAccountClass_SelectedIndexChanged(object sender, EventArgs e)
        {
            try
            {
                AccountClassInfo ac = ddlAccountClass.SelectedItem as AccountClassInfo;
                CompanyParamInfo comParam = new CompanyParamInfo();
                comParam.TargetReport = ReportName.RE_05;
                comParam.AccountClassId = ac.AccountClassId;
                _presenter.LoadCompanyList(comParam);
            }
            catch (Exception ex)
            {
                WaitingFormHelper.HideWaitingForm();
                Logger.WriteError(Logger.Module.EPAYMENT, "แสดงข้อมูล Company RE_06", ex.ToString());
                MessageBox.Show("ไม่สามารถติดต่อกับฐานข้อมูลได้ โปรดติดต่อผู้ดูแลระบบ\n", "ข้อผิดพลาด",
                   MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
        }

        private void txtPeriod_KeyDown(object sender, KeyEventArgs e)
        {
            if (e.KeyCode == Keys.Enter)
            {
                btnReport.Focus();
            }
        }
    }
}

