using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using System.Collections.Generic;
using System.Windows.Forms;
using PEA.BPM.ePaymentsModule.Interface.Services;
using PEA.BPM.ePaymentsModule.Interface.Constants;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities;


namespace PEA.BPM.ePaymentsModule
{
    public class DebtClearifyViewPresenter : Presenter<IDebtClearifyView>
    {
        private IBillingService _billingService;
       
        [InjectionConstructor]
        public DebtClearifyViewPresenter([ServiceDependency] IBillingService billingService)
		{
            _billingService = billingService;
		}

        [EventPublication(EventTopicNames.SearchDebtPopUp, PublicationScope.WorkItem)]
        public event EventHandler<EventArgs<ClearifyInfo>> SearchDebtPopUp;        
        internal void OnSearchDebtPopUp(ClearifyInfo clearifyItem)
        {
            if (SearchDebtPopUp != null)
                SearchDebtPopUp(this, new EventArgs<ClearifyInfo>(clearifyItem));
        }

        public bool SaveClearify(SaveClearifyInfo saveClearifyItem)
        {
            return _billingService.SaveClearify(null, saveClearifyItem);
        }
        


        [EventSubscription(EventTopicNames.GetDebtDetail, Thread = ThreadOption.UserInterface)]
        public void GetPaymentDetailHandler(object sender, EventArgs<ClearifyInfo> e)
        {
            ClearifyInfo clearifyItem =  (ClearifyInfo)e.Data;            
            View.ViewDebtDetail(clearifyItem);
        }

        [EventSubscription(EventTopicNames.SearchDebtResult, Thread = ThreadOption.UserInterface)]
        public void GetSearchDebtResultHandler(object sender, EventArgs<BPMClearifyInfo> e)
        {
            View.ViewDebtSearchResult(e.Data);
        }

        

        /// <summary>
        /// This method is a placeholder that will be called by the view when it's been loaded <see cref="System.Winforms.Control.OnLoad"/>
        /// </summary>
        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        /// <summary>
        /// Close the view
        /// </summary>
        public void OnCloseView()
        {
            base.CloseView();
        }

    }
}

