using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.ePaymentsModule.Interface.Constants;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities;
using PEA.BPM.ePaymentsModule.Interface.Services;
using System.Collections.Generic;

namespace PEA.BPM.ePaymentsModule
{
    public class UploadFileViewPresenter : Presenter<IUploadFileView>
    {
        private IBillingService _billingService;
       
        [InjectionConstructor]
        public UploadFileViewPresenter([ServiceDependency] IBillingService billingService)
		{
            _billingService = billingService;
        }

        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        public void OnCloseView()
        {
            base.CloseView();
        }
         

        #region Publication Event

        [EventPublication(EventTopicNames.GetEPaymentUpload , PublicationScope.WorkItem)]
        public event EventHandler<EventArgs<List<EpayUploadItem>>> GetEPaymentUpload;
        internal void OnGetEPaymentUpload(List<EpayUploadItem> param)
        {
            if (GetEPaymentUpload != null)
                GetEPaymentUpload(this, new EventArgs<List<EpayUploadItem>>(param));
        }

        [EventPublication(EventTopicNames.GetGroupCompany, PublicationScope.WorkItem)]
        public event EventHandler<EventArgs<List<Company>>> GetGroupCompany;
        internal void OnGetGroupCompany(List<Company> param)
        {
            if (GetGroupCompany != null)
                GetGroupCompany(this, new EventArgs<List<Company>>(param));
        }

        #endregion

        #region Subscription Event

        [EventSubscription(EventTopicNames.ResetFileName, Thread = ThreadOption.UserInterface)]
        public void GetEPaymentUploadHandler(object sender, EventArgs e)
        {
            View.ClearFileName();
        }

        #endregion

        #region Internal Function


        internal bool IsUploadFileNameExist(string fileName)
        {
            try
            {
                return _billingService.IsUploadFileNameExist(fileName);
            }
            catch (Exception Ex)
            {
                throw;
            }
        }

        internal List<EpayUploadItem> GetDebtComparable(string caInvoiceNo)
        {
            try
            {
                return _billingService.GetDebtComparableService(caInvoiceNo);
            }
            catch (Exception Ex)
            {
                throw;
            }
        }

        internal List<Company> GetCompany(Company comp)
        {
            try
            {
                return _billingService.SearchCompany(comp);
            }
            catch (Exception Ex)
            {
                throw;
            }
        }
        #endregion

    
       

    }
}

