using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using System.Collections.Generic;
using System.Windows.Forms;
using PEA.BPM.ePaymentsModule.Interface.Services;
using PEA.BPM.ePaymentsModule.Interface.Constants;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities;


namespace PEA.BPM.ePaymentsModule 
{
    public class VendorPaymentViewPresenter : Presenter<IVendorPaymentView>
    {
        private IBillingService _billingService;
       
        [InjectionConstructor]
        public VendorPaymentViewPresenter([ServiceDependency] IBillingService billingService)
		{
             
            _billingService = billingService;

		}  

        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        public void OnCloseView()
        {
            base.CloseView();
        }

        #region Event Subscription

        [EventSubscription(EventTopicNames.SearchAgentPayment, Thread = ThreadOption.UserInterface)]
        public void SearchAgentPaymentHandler(object sender, EventArgs<AgentPayment> e)
        {
            List<AgentPayment> agentPayList =  _billingService.GetAgentPaymentService(e.Data);
            if (agentPayList.Count > 0)
            {
                View.ResetAll();
                View.SetAgentPaymentData(agentPayList);
            }
            else
            {
                View.ResetAll();
                MessageBox.Show("ไม่พบข้อมูลของตัวแทนตามเงื่อนไขที่ค้นหา", "ผลการค้นหา", MessageBoxButtons.OK, MessageBoxIcon.Information);
            }
        }

        #endregion

        #region Internal Function

        internal void InsertAgentPayment(List<AgentPayment> agentPayList)
        {
            try
            {
                _billingService.InsertAgentPaymentService(agentPayList);
            }
            catch (Exception Ex)
            {
                throw;
            }
        }

        #endregion
    }
}

