using System;
using Microsoft.Practices.ObjectBuilder;
using Microsoft.Practices.CompositeUI;
using PEA.BPM.Infrastructure.Interface;
using Microsoft.Practices.CompositeUI.EventBroker;
using PEA.BPM.ePaymentsModule.Interface.Constants;
using PEA.BPM.ePaymentsModule.Interface.BusinessEntities;
using PEA.BPM.ePaymentsModule.Interface.Services;
using System.Collections.Generic;

namespace PEA.BPM.ePaymentsModule 
{
    public class VendorSearchViewPresenter : Presenter<IVendorSearchView>
    {
        
        private IBillingService _billingService;
       
        [InjectionConstructor]
        public VendorSearchViewPresenter([ServiceDependency] IBillingService billingService)
		{
            _billingService = billingService;
		}

        public override void OnViewReady()
        {
            base.OnViewReady();
        }

        public void OnCloseView()
        {
            base.CloseView();
        }

        #region Publication Event

        [EventPublication(EventTopicNames.SearchAgentPayment, PublicationScope.WorkItem)]
        public event EventHandler<EventArgs<AgentPayment>> SearchAgentPayment;
        internal void OnSearchAgentPayment(AgentPayment param)
        {
            if (SearchAgentPayment != null)
                SearchAgentPayment(this, new EventArgs<AgentPayment>(param));
        }

        #endregion

    }
}

